h3 {
    margin: 0 .5vh
}
.avatar-image, .sleeve-image {
    border-width: 1px;
    border-style: dashed;
}
.avatar-image[data-v-7d3c70a7], .sleeve-image[data-v-7d3c70a7] {
    max-height: 200px;
