}
#profile-ranking-table td[data-v-7d3c70a7] {
    padding: 0
}
.file-picker-container[data-v-7d3c70a7], .remove-custom-button[data-v-7d3c70a7] {
    font-size: 2vh;
    line-height: 3;
    height: auto;
    min-height: 0;
    width: 300px;
    max-width: 100%;
