    animation: thistab 2s;
    font-size: 0;
    width: 8vh;
    vertical-align: top;
}
}
@media (orientation: portrait) {
div:not(#app), #leaderboard-page {
    display: none
}
}
@keyframes thistab {
    from {
        font-size: 3vh;
        width: 22vh;
    }
    to {
        font-size: 0;
        width: 8vh;
    }
}
}

@-moz-document url-prefix("https://duelingnexus.com/game"), url-prefix("https://duelingnexus.com/editor") {
/*SECTION 09 GAMEGLOBAL*/
/*This section applies to the game and editor pages. Much of the code is shared.*/
div {
    line-height: 1
}
h4 {/*Big headers.*/
    font-size: 3vh
}
h6 {/*Small headers.*/
    font-size: 1.5vh
}
#editor-container, #game-container, .engine-dark-box {
    margin: 0
}
#card-column, #editor-decks-column, #game-siding-column {
    height: 100vh
}
#card-column {/*Moves the left column to the left wall of the screen.*/
    width: 24vw;
    float: left;
    margin: 0 .5vw;
}
#editor-decks-column, #game-siding-column {
    width: 35.5vw;
    padding: 0;
}
#editor-search-column, #game-siding-column-2 {
    height: 40vw;
    width: 38.5vw;
    overflow: hidden;
    margin: 0 .5vw;
    padding: 0;
    position: relative;
}
.engine-dark-box { 
    min-height: 2vw
}
.engine-text-box, #card-description-box, #editor-main-deck, #siding-main-deck, #editor-extra-deck, #editor-side-deck, #siding-extra-deck, #siding-side-deck {
    border-width: .15vw
}
.engine-dark-box, #card-column, #card-name, #card-picture-container, #card-description-box, #editor-main-deck, #siding-main-deck, #editor-extra-deck, #editor-side-deck, #siding-extra-deck, #siding-side-deck, .engine-text-box, #editor-search-results {
    box-sizing: border-box
}
#card-name, #editor-deck-main-title, #editor-search-title, #siding-deck-main-title,#editor-deck-extra-title, #editor-deck-side-title, #siding-deck-extra-title, #siding-deck-side-title {
	margin: 0
}
#card-picture-container, #editor-main-deck, #siding-main-deck {
    height: 20.5vw !important;
    min-height: 0;
    max-height: 100vh;
    margin: 1vw 0 .5vw 0;
}
#card-picture {/*"%" represents what percentage of the #card-column this image file takes up.*/
    height: 100%;
    width: auto;
    max-width: 100%;
}
#card-description-box {
    font-size: 2vh;
    text-align: justify;
    cursor: s-resize;
    overflow-x: hidden;
    overflow-y: auto;
    height: 10.75vw;
    padding: .5vh 1vh;
}
#card-if-monster, #card-if-spell, #card-if-trap {
    -webkit-transition: .5s ease-in-out;
    -moz-transition: .5s ease-in-out;
    -ms-transition: .5s ease-in-out;
    -o-transition: .5s ease-in-out;
    transition: .5s ease-in-out;
    font: bold 2vh sans-serif;
}
#card-if-monster:hover, #card-if-spell:hover, #card-if-trap:hover {
    -webkit-transition: .5s ease-in-out;
    -moz-transition: .5s ease-in-out;
    -ms-transition: .5s ease-in-out;
    -o-transition: .5s ease-in-out;
    transition: .5s ease-in-out;
    font-size: 1.25vw;
}
#editor-extra-deck, #editor-side-deck, #siding-extra-deck, #siding-side-deck {
    height: 5.25vw !important;
    margin: .5vw 0;
}
.engine-text-box {
    font-size: 1.5vw;
    height: 2.3vw;
    padding: .15vw;
    border-color: #AAAAAA;
    border-width: .15vw;
}
/*ENGINE BUTTONS*/
.engine-button:not(.game-phase-button) {
    -moz-transition-duration: 2s;
    -o-transition-duration: 2s;
    transition-duration: 2s;
    border-width: .15vw;
    cursor: pointer;
    min-height: 0;
    padding: 0 .5vw;
}
.engine-button:active {
    -moz-transition-duration: 1s;
    -o-transition-duration: 1s;
    transition-duration: 1s;
    font-style: italic;
}
table#game-field {
    margin-left: 1.5vw;
}
}

@-moz-document url-prefix("https://duelingnexus.com/game") {
/*SECTION 10 GAME*/
/*This section affects only the game pages.*/
.engine-window p, #game-chat-area, #game-chat-area .engine-text-box, #game-end-text, #game-end-title {
    margin: 0;
    padding: 0;
}
#game-to-spectator-button, #game-to-duelist-button, #game-deck-dropdown, .template-button, #game-rps-first, #game-rps-second, #game-end-button {
    width: auto;
    max-width: 28vw;
}
.engine-button:not(.game-phase-button) {
    font-size: 3.5vh
}
/*Chat*/
#card-column {
    margin-top: .5vw
}
#game-chat-area {/*This is the box that contains the chat to a confined space. It's the heart of this Stylish.*/
    font-size: 2vh;
    border-width: .1vw;
    border-style: dashed;
    box-sizing: border-box;
    overflow-x: hidden;
    overflow-y: scroll;
    height: 10vw;
    width: 24vw;
    min-width: 0;
    position: fixed;
    bottom: 40px;
    left: .5vw;
}
#game-chat-area .engine-text-box {/*This is the smaller box where you type into.*/
    font-size: 2vh;
    border-width: 0.375vw;
    border-style: double;
    box-sizing: border-box;
    height: 4vh;
    width: 22.22vw;
    position: fixed;
    bottom: 45px;
    left: .75vw;
}
.game-avatar-area {/*This ensures that the left-most username doesn't enter the #card-column.*/
    font-size: 1vw;
    text-align: center;
    word-break: break-all;
    width: 81%;
}
/*OPTIONS*/
#options-area, #options-window {
    position: fixed;
