css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dvice.com") {
  .ColumnsWrapper .ColumnOne, 
  .ColumnsWrapper .ColumnThree, 
  .BlogHeader, 
  #BlogSearch, 
  .ColumnTwoInterior > div:first-child, 
  .ColumnEndCap, 
  #AdInterstitial, 
  .BlogFooter
  {
    display: none !important;
  }

  .ColumnsWrapper {background: none !important;}
  .ColumnTwo, .ColumnTwoInterior {width: 100% !important; margin-left: 10px !important;}
}
