@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("streaming-world.fr") {

/* ==== 0- Stream - Streaming-world.fr Widescreen v.2 - WATnew06 - new ==== */

/* ==== LINK:  
http://www.streaming-world.fr/#id
==== */

/* SUPP */

/* #menu_droite  , */
html>body[onload="window.location.href = '#id';"] .oModal,
#menu_gauche1 {
    display: none !important;
}

/* WIDESSCREEN */

#bloc_page ,
#blocpage2 {
    width: 99% !important;
    padding: 0 !important;
}


html>body>header>center ,

#blocpage2>header>center ,
#blocpage2>header {
    height: 30px !important;
}
html>body>header>center>img ,

#blocpage2>header>center>img {
    vertical-align: middle;
    width: 122px !important;
}


#corps1 {
    width: 100% !important;
}

#corps1> #titre_film {
float: right !important;
clear: both !important;
    width: 30% !important;
}
#corps1> #titre_film + center{
float: right !important;
clear: both !important;
    width: 30% !important;
}
#corps1> #titre_film + center>img {
    height: auto !important;
    width: 530px !important;
    margin-left: -23px !important;
}

#corps1 #synopsis_film {
float: left !important;
    width: 34% !important;
margin-top: -35px !important;
padding-left: 630px !important;
text-align: left !important;
}





/* ==== END  ==== */

}

@-moz-document url-prefix("http://www.streaming-world.fr/modules/video/lecture_video.php?id_serie=") {

/* START - URL PREF - SERIE PLAYER ==== */

/* SUPP */
#corps1>center:last-of-type {
display: none !important;
}
#corps1 {
    background-color: #181818;
    border: 1px solid #ffffff;
    float: left;
    margin-right: 10px;
    padding: 5px;
    width: 35% !important;
border: 1px solid red !important;
}
#corps1 > #titre_film {
    clear: both;
    float: left !important;
    width: 97% !important;
}
#menu_droite {
    display: inline-block !important;
    float: left;
    min-width: 100px !important;
    max-width: 100px !important;
    height: 470px !important;
    margin-bottom: 0;
    padding: 10px !important;
    overflow: hidden !important;
    overflow-y: auto !important;
}
#blocpage2>footer {

    float: right !important;
    clear: none !important;
    width: 55% !important;
}
/* END URL PREF - SERIE PLAYER  === */
}

@-moz-document url-prefix("http://www.streaming-world.fr/modules/video/fiche_serie.php?id_serie") {

/* START - URL PREF - SERIE INTRO ==== */

/* SUPP */
/* #corps1>center:last-of-type {
display: none !important;
} */

/* WIDESCREEN */
#blocpage2 #corps1 {
    float: left;
    clear: none !important;
    width: 90% !important;
    margin-right: 10px;
    margin-bottom: 26px !important;
    padding: 5px;
border: 1px solid red !important;
}
#blocpage2 #corps1 > #titre_film {
    clear: both;
    float: left !important;
    width: 98% !important;
}

#blocpage2 #corps1 > #titre_film + center {
    clear: both;
    float: right;
    width: 31% !important;
}
#blocpage2 #corps1 > #titre_film + center > img {
    height: auto;
    margin-left: -23px;
    width: 530px;
}
#blocpage2  #corps1 #synopsis_film {
    float: left;
    margin-top: 0px !important;
    margin-left: 0px !important;
    padding: 15px 15px 15px 704px !important;
    text-align: left;
    width: 25% !important;
}
/* #synopsis_film>center{
float: left !important;
    width: 50% !important;
margin-left: -625px !important;
margin-top: -200px !important;
} */
#synopsis_film>center iframe {
float: left !important;
    min-width: 100% !important;
margin-left: -0655px !important;
margin-top: -370px !important;
}


#menu_droite {
    display: inline-block !important;
    float: right !important;
    clear: none !important;
    min-width: 130px !important;
    max-width: 130px !important;
    height: 770px !important;
    margin-bottom: 0;
    padding: 10px !important;
    overflow: hidden !important;
    overflow-y: auto !important;
}


#blocpage2>footer {
    float: none !important;
    clear: none !important;
    width: 94.7% !important;
    margin-left: -10px !important;
/* margin-top: 80px !important; */
}


/* END URL PREF - SERIE INTRO  === */
}
