css:
@-moz-document url-prefix("https://qa.designer.veritran.com"), 
url-prefix("http://vtdesigner-train.veritran.net"),
url-prefix("http://vtdesignerco01-dev01.veritran.net") {

form table.table-conf tbody td,
form table.table-conf caption,
form table.table-conf thead th,
form table.table-conf2 tbody td,
form table.table-conf2 caption,
form table.table-conf2 thead th,
table.dataTable > thead > tr > th:hover,
.table-conf2 tbody td,
.dataTableV2 th,
.content-box,
.subtitulo,
#bodyBar
{
	background-color: #333 !important;
    color: #fff !important;
}

table.dataTableV2,
table.dataTable tr,
table.dataTable > thead > tr > th{
	color: #111 !important;
}

form table.table-conf input, 
form table.table-conf select, 
form table.table-conf textarea,
form table.table-conf2 input, 
form table.table-conf2 select, 
form table.table-conf2 textarea{
    background: #222 !important;
    color: #fff !important;
    width: 80%;
    border: 1px solid #696c6f !important;
}


form table.table-conf tbody td,
form table.table-conf thead th,
form table.table-conf tbody tr:last-child td
{
    border-bottom: solid 1px #555 !important;
}

form table.table-conf caption,
form table.table-conf,
.table-conf tbody tr, .table-outer tbody tr {
    border: 1px solid #555 !important;
}

table.table-conf tbody tr:hover td {
    background: #666 !important;
}

div.dataTables_wrapper tr.row_selected, 
div.dataTables_wrapper tr.row_selected:hover {
    background-color: #cecece!important;
    color: #454545!important;
    font-weight: bold;
}

div.dataTables_wrapper tr:hover {
    background-color: #d9d9d9!important;
}

.memu-icon { 
    border: 1px solid #001e27 !important;
    background-color: #001219;
    box-shadow: 3px 3px 2px rgba(0,0,0,0.25);
    -webkit-filter: brightness(3);
    filter: brightness(3);
}

.memu-icon:hover { 
    border: 1px solid #222 !important;
    background-color: #333;
    filter: contrast(2) brightness(2);
}

.submenu li a {
	border: 1px solid #666;
    box-shadow: 3px 3px 2px rgba(0,0,0,0.6);
    background: #fff;
    filter: contrast(2) brightness(0.9);
}

.ui-state-default, 
.ui-widget-content .ui-state-default, 
.ui-widget-header .ui-state-default {
    background: #ccc;
}

.submenu li a:hover {
	border: 1px solid #999;
    box-shadow: 3px 3px 2px rgba(0,0,0,0.9);
    background-color:#ccc;
}

#modalIframe {width:95%; height:100%;}
#contentIframe {width:100%; height:100%;}
form table.table-conf button {
    border: 1px solid #2e5f77 !important;
    background-color: #003848 !important;
}

#launcher {display:none}

.page-tabs .tab {
    font-size: 12px !important;
}

.page-tabs .tab.unsaved {
    border-top: 2px solid #f00 !important;
}

.page-tabs .tab.active {
    background-color: #c3c3c3 !important;
    border-top: 2px solid #15b2ff !important;
    color: #00486b !important;
    font-weight: bold;
}

.attribute-width-small .container-box {
	width:100% !important;
}

}
