* { font-family: Terminus, monospace !important; }

a { color: lightgrey !important; }

font[color="green"] { color: #00cc00 !important; }

img[alt="NetHack"] {
width: 0 !important;
height: 0 !important;
padding-left: 277px !important;
padding-top: 104px !important;
background: #000 url(http://i42.tinypic.com/2i9o96b.png) !important;
}

/* This currently disables the image map but since the navigation links are just below it, it's not much of a loss. */
img[alt="Screen Shot"] {
width: 0 !important;
height: 0 !important;
padding-left: 579px !important;
padding-top: 276px !important;
background: #000 url(http://i43.tinypic.com/2nqgkfb.jpg) !important; /* Why did tinypic.com save this as a jpg!? */
border-color: darkgrey !important;
}

td { border: 1px solid darkgrey !important; }

}
