css:
@-moz-document domain("konkosite.com") {
/* Header */
.wpcnt,
.navbar,
.dropdown-menu {
    background-color: #171717 !important;
}
.navbar a:link,
.navbar a:visited,
.navbar-brand {
    color: #eee !important;
}
.navbar a:hover {
    background-color: #303030 !important;
}
}

@-moz-document domain("konkosite.com") {
/* Body */
.hfeed {
    background-color: #171717 !important;
}
.post-content {
    background-color: #232323 !important;
}
.entry-title,
.entry-title a {
    color: lightslategray !important;
    font-style: italic !important;
}
.entry-content,
.entry-summary {
    color: #eee !important;
}

/* Body - Sidebar */
.widget{
    background-color: #232323 !important;
}
.search-field,
input#subscribe-field-blog_subscription-3 {
    background-color: #171717 !important;
    border-color: #303030 !important;
}
.search-submit,
#subscribe-submit button {
    background-color: #303030 !important;
    border-color: #303030 !important;
}
.widget-title {
    border-bottom: 1px solid #505050 !important;
    color: #bbb !important;
}
select#amount,
.wpapp_other_amt_input,
.wp_pp_button_reference {
    background-color: #171717 !important;
    border-color: #171717 !important;
    color: #ccc !important;
}
.widget_recent_entries li,
.wpapp_payment_button input[type="image"] {
    border-color: #505050 !important;
}

/* Body - Comments */
.post-comments,
.comments-area {
    background-color: #232323 !important;
}
.comments-title {
    color: lightslategray !important;
    font-style: italic !important;
}
.says,
.comment-content,
.comment-author {
    color: #bbb !important;
}
/* Body - Footer */
.nav-links,
.nav-links div {
    background-color: #232323 !important;
}
.prev-post a,
.next-post a {
    color: #aaa !important;
}
}
