css:
@-moz-document url-prefix("https://funpay.ru/") {
.wrapper {  background: url(https://wallpaperaccess.com/full/2047906.jpg) top fixed; /* просто замени  ссылку */
}
.content-orders
{
background: url(https://wallpaperaccess.com/full/2047906.jpg) top fixed; /* просто замени  ссылку */
}
body {
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
    font-size: 14px;
    line-height: 1.428571429;
    color: #ff8949;
    background: url(https://wallpaperaccess.com/full/2047906.jpg) top fixed; /* просто замени  ссылку */
}
.profile-cover-img {
    background-clip: border-box;
    background: url(https://wallpaperaccess.com/full/755331.jpg) center; /* Полусфера в профиле. */
    background-size: 55% auto;
}
.cd svg
{
        background: url(https://wallpaperaccess.com/full/755331.jpg) no-repeat center; /* все сферы везде. */
        background-size: 295% auto;
}
.profile-cover {
    overflow: unset;
}
}

@-moz-document url-prefix("https://funpay.ru/") {
body {
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
    font-size: 14px;
    line-height: 1.428571429;
    color: #ff8949;
    /* Цвет текста основной */
    background-color: #000;
}
a {
    color: #54c7a3;
    /* Цвет текста надпись продавец\аккаунты\услуги\*** */
    text-decoration: none;
}
}

@-moz-document url-prefix("https://funpay.ru/") {
.panel {
    background-color: #423e3e61;
}
}

@-moz-document url-prefix("https://funpay.ru/") {
.panel-default > .panel-heading {
    background-color: #292929;
    border-color: #2d2d2d61;
}
}

@-moz-document url-prefix("https://funpay.ru/") {
.panel-default > .panel-heading {
    color: #66b7ff;
    /* Цвет текста чат с продавцом */
}
}

@-moz-document url-prefix("https://funpay.ru/") {
.form-control {
    background-color: #423d3d;
    color: #54c7a3;
    /* Цвет текста надпись в выборе оплаты QIWI и блаблабла */
}
}

@-moz-document url-prefix("https://funpay.ru/") {
.navbar-default {
    background-color: #41d2bc00;
}
}

@-moz-document url-prefix("https://funpay.ru/") {
.navbar-default .navbar-nav > li > a {
    color: #54c7a3;
    /* Цвет текста надпись верхней строки (мои покупки итд) */
}
}

@-moz-document url-prefix("https://funpay.ru/") {
.nav-tabs > li.active > a,
.nav-tabs > li.active > a:hover,
.nav-tabs > li.active > a:focus {
    color: #19d3ff;
    /* Цвет текста в кнопке выбранного  раздела */
    background-color: #000000;
}
}

@-moz-document url-prefix("https://funpay.ru/") {
table.table-clickable tbody tr a {
    color: #14e6a4;
    /* Цвет ников продавцов */
    text-decoration: none;
}
}

@-moz-document url-prefix("https://funpay.ru/") {
.text-muted {
    color: #bce6cf;
    /* Цвет текста под никами. (на сайте 1337 лет итд) */
}
}

@-moz-document url-prefix("https://funpay.ru/") {
.form-control[disabled],
.form-control[readonly],
fieldset[disabled] .form-control {
    background-color: #484343;
    /* бокс заплачу */
}
}

@-moz-document url-prefix("https://funpay.ru/") {
.chat-contacts, .chat-detail {
    background: #26272d;
    border: #90f 2px solid;
}
.contact-item {
    border-bottom: #000 1px;
}
.chat-full-header {
    border-bottom: #90f 1px solid;
}
.chat {
    border-bottom: 1px solid #90f;
}
.chat {
    border-top: 1px solid #90f;
}
.alert-info {
    background-color: #ff002921;
    border-color: #ffef00;
    color: #ffffffe0;
}
.custom-scroll::-webkit-scrollbar, .chat-message-list::-webkit-scrollbar, .chat-empty::-webkit-scrollbar, .chat-form-input .form-control::-webkit-scrollbar, .chat-form-input .hiddendiv::-webkit-scrollbar {
    background: #e600ff29;
        width: 1px;
    height: 1px;
}
.custom-scroll::-webkit-scrollbar-thumb, .chat-message-list::-webkit-scrollbar-thumb, .chat-empty::-webkit-scrollbar-thumb, .chat-form-input .form-control::-webkit-scrollbar-thumb, .chat-form-input .hiddendiv::-webkit-scrollbar-thumb {
    background: #ada1ff96;
}
}

@-moz-document url-prefix("https://funpay.ru/") {
.bg-light-color #header, .bg-light-color .wrapper-footer {
      background: transparent;
}
.chat-full .chat {
    background-color: #0000007a;
}
.media-user-name {
    color: #00ccffb5;
}
.chat {
    background-color: #0000008f;
}
.media-user-name a {
    color: #00ffb3c4;
}

.bg-light-color {
    background-color: #fff0
}
.game-title a {
    color: #ffaa00ed;
    text-decoration: none;
}
.wrapper-content {
    background: #00000057;
}
.navbar-right.logged .dropdown-menu {
    background-color: #282828db;
}
.user-link-name {
    color: #00ffb3c4;
}
}

@-moz-document url-prefix("https://funpay.ru/") {
.content-with-cd-wide {
    background: #0000007a;
    border: 1px solid #feff00;
    border: 1px solid rgba(236, 247, 18, .16);
    border-radius: 30px;
    outline: 0;
}
a.tc-item {
    color: #00ffb3c4;
    text-decoration: none;
}
.cd {
    position: relative;
    z-index: 100;
    border-radius: 50%;
    width: 400px;
    height: 400px;
}
a.cd-satellite
{
     color: #00ffb300;
     z-index: 1;
    border-radius: 1%;
    width: 1px;
    height: 1px;
        background: #4384d000;
}
.offer {
    background: #0000003b;
}
.tc.table-hover .tc-item.warning {
    background-color: #9e1c1c29;
}
.tc
{
    background-color: #0000004a
}
}

@-moz-document url-prefix("https://funpay.ru/") {
.modal-content {
    background-color: #000c;
    border: 1px solid #999;
    border: 1px solid rgba(236, 247, 18, .16);
    border-radius: 60px;
    -webkit-box-shadow: 0 3px 9px rgba(0,0,0,0.5);
    box-shadow: 0 3px 9px rgba(0,0,0,0.5);
    background-clip: padding-box;
    outline: 0;
}
label.control-label
{
    color: #ffba4cc4
}
.counter-item {
    background: #f3a0291c;
}
.counter-item.active {
    background: #f3a02994;
    color: #00d9ff;
}
.form-control-box {
    background: #84848400;
    border: 1px solid #84848400;
}
h5, .h5, .form-group>label
{
    color: #14e6a4bf
}
.bootstrap-select .dropdown-toggle .filter-option {
    background-color: #595a46ab;
}
.bootstrap-select .dropdown-menu.inner {
       background-color: #595a46c2;
}
}

@-moz-document url-prefix("https://funpay.ru/") {
.chat-box {
    border-radius: 55px;
    background: #00000078;
}
.dropdown-menu {
    background-color: #282828db;
}
.navbar-default {
    border-color: #e4e4e400;
}
.btn-gray:hover {
    background-color: #ff6c1173;
        color: #00ffefd6
}
.btn-gray {
    background-color: #ff6c1130;
    color: #00ffefd6   
}
.btn-primary
{
     background-color: #ff6c1130;
        color: #00ffefd6
}
.btn-primary:hover
{
     background-color: #ff6c1173;
        color: #00ffefd6
        
}
.btn-primary:active:hover, .btn-primary:active:focus, .btn-primary:active.focus, .btn-primary.active:hover, .btn-primary.active:focus, .btn-primary.active.focus, .open>.btn-primary.dropdown-toggle:hover, .open>.btn-primary.dropdown-toggle:focus, .open>.btn-primary.dropdown-toggle.focus {
    color: #00ffefd6;
    background-color: #ff6c1173;
    border-color: #00ffefd6;
}

.btn-default
{
     background-color: #ff6c1130;
        color: #00ffefd6
}
.btn-default:hover
{
     background-color: #ff6c1173;
        color: #00ffefd6     
}
.chat-contacts, .chat-detail {
    background: #00000073;
}
}

@-moz-document url-prefix("https://funpay.ru/") {
.withdraw-box .slave {
    background-color: #3e3e3e;
}
.tc.table-hover .tc-item.transaction-status-waiting {
    background-color: #ffed0052;
}
.btn-default.dropdown-toggle, .btn-default.dropdown-toggle:hover, .btn-default.dropdown-toggle:focus, .btn-default.dropdown-toggle:active, .open>.btn-default.dropdown-toggle, .open>.btn-default.dropdown-toggle:hover, .open>.btn-default.dropdown-toggle:focus, .open>.btn-default.dropdown-toggle:active {
    color: #00ffefd6;
}
.tc-finance .tc-header>div, .tc-finance .tc-item>div {
    border-top: #42424254 1px solid;
}
.tc.table-hover .tc-item.info {
    background-color: #ecf3fa4a;
}
.tc.table-hover .tc-item:hover {
    background-color: #ffd57363;
}
.navbar-default .navbar-nav>.active>a, .navbar-default .navbar-nav>.active>a:hover, .navbar-default .navbar-nav>.active>a:focus {
    color: #00ff8a;
    background-color: rgba(0,0,0,0);
}
}
