@-moz-document url-prefix("https://www1.flightrising.com/auction-house/sell/") {
/*—————————————————————————————————————————————————————————————————*/
/*
                         [OPTION 1]

  searches are case insensitive and will match partial words.
           you can use spaces in your search terms.

/*—————————————————————————————————————————————————————————————————*/

/*leave this part alone*/
.itemicon.itemicon-small.ah-sell-item {
    display:none;
}

/* Edit the value between the quotes below to search by item name: */
.itemicon.itemicon-small.ah-sell-item[data-name*="editMe" /*i*/] {
    display:inline-block;
}
}

@-moz-document url-prefix("https://www1.flightrising.com/auction-house/sell/") {
/*—————————————————————————————————————————————————————————————————*/
/*
                         [OPTION 2]

  searches are case insensitive and will match partial words.
           you can use spaces in your search terms.

/*—————————————————————————————————————————————————————————————————*/

/*leave this part alone*/
.itemicon.itemicon-small.ah-sell-item {
    opacity:0.5;
}

/* Edit the value between the quotes below to search by item name: */
.itemicon.itemicon-small.ah-sell-item[data-name*="editMe" /*i*/] {
