css:
@-moz-document domain("onedayonly.co.za") {
h1,
h2,
h3,
h4,
h5 {
    color: #eee !important;
}
p,
li {
    color: #eee !important;
}
section {
    background: #343434 !important;
}

/*********  Main menu   ***********************************************/
#mainmenu a {
    color: #ddd !important;
}
#mainmenu a:hover {
    background: #1a6c29 !important;
}

/*********  Home   ***********************************************/
body,
#page {
    background: #262626 !important;
    color: #ddd !important;
}
#page > #home > .block_wrapper {
    display: none !important;
}
h2.shop_name {
    background: #262626 !important;
}
#wrapper > #page > #home > .block_wrapper.product_block {
    display: block !important;
    flex: 0 0 25% !important;
    min-width: auto !important;
}
#wrapper > #page > #home > .block_wrapper.shop_block {
    display: block !important;
    flex: 1 0 100% !important;
    min-width: auto !important;
}
.block_wrapper .new_product_block .details {
    display: block !important;
    padding: 5px 10px !important;
    height: auto !important;
}
.block_wrapper .new_product_block .details > div {
    display: flex !important;
    align-items: center !important;
}
.block_wrapper .new_product_block .details .savings {
    display: none !important;
}
.details .titles {
    padding: 0 !important;
    flex: 1 0 !important;
    margin: 0 !important;
}
.titles .brand {
    display: none !important
}
.titles .name {
    text-overflow: ellipsis !important;
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    overflow: hidden !important;
    padding-right: 10px;
}
.details:hover .titles .name {
    display: -webkit-box;
    -webkit-line-clamp: 3;
    -webkit-box-orient: vertical;
}
.block_wrapper .overlay {
    display: none !important;
}
.block_wrapper .new_product_block {
    position: relative !important;
}
.block_wrapper .quantity_left {
    position: absolute !important;
    right: 0 !important;
    top: -24px !important;
}
.block_wrapper .quantity_left h6 {
    margin: 0px !important;
    padding: 5px 10px;
    background: #006dbf;
    color: white;
}
.block_wrapper .new_product_block .details .prices {
    padding: 0 !important;
    flex: 0 0 !important;
    margin: 0 !important;
}
.block_wrapper .new_product_block .details .prices .selling {
    font-size: 20px !important;
}
.line_one {
    line-height: 10px !important;
}
.line_one .retail {
    font-size: 10px !important;
    display: inline-block !important;
    white-space: nowrap !important;
    margin: 0 !important;
}
.block_wrapper .new_product_block .details .new_button {
    display: none !important;
}
.categories-container,
#testimonial,
#footer {
    display: none !important;
}
::-webkit-scrollbar {
    height: 12px;
    width: 12px;
    background: #000 !important;
}
::-webkit-scrollbar-thumb {
    background: #808080 !important;
}
::-webkit-scrollbar-corner {
    background: #000 !important;
}

/*********  Products   ***********************************************/
.gm-t,
.gd-t,
.gl-t {
    color: #bbb !important;
}

#forward,
#back {
    filter: invert(50%) !important;
    background: none !important;
}
}
