css:
@-moz-document domain("www.youtube.com")
{
    .yt-card
  {
    margin: 0 0 10px;
    border: 0;
    background: rgba(0, 0, 0, 0.49);
    box-shadow: 0 1px 2px rgba(0, 0, 0, .1);
    -moz-box-sizing: border-box;
    box-sizing: border-box;
  }
}

@-moz-document regexp("https?://www.youtube.com?/(?!my_videos|ad_companion|subscribe_embed|account|yt|red).*")
{
  html:not(#⁠),body>div,body>table,[role="navigation"],[role="dialog"],#body-container>div,[aria-expanded="true"]:not(.ytp-button),[class*="tooltip"]:not(.ytp-tooltip-duration):not(.ytp-tooltip-bg):not(.ytp-tooltip-text-wrapper):not([class*="arrow"]):not(button),[role="menu"],.ytp-popup,.videoAdUiPreSkipButton,.videoAdUiAdInfoPopup
  {
    background: url(https://image.noelshack.com/fichiers/2019/30/5/1564126928-maxresdefault-1.png)fixed #000!important;
    background-size: cover!important;
  }
  
/*search bar*/  #search-terms, #search-input.ytd-searchbox-spt, #container.ytd-searchbox
  {
    border: 1px solid!important;
    color: #ea1d07!important;
  }

  #masthead-search-terms.gsfe_a
  {
    border: 2px solid!important;
    color: #ea1d07!important;
  }

  #masthead-search-terms.gsfe_b
  {
    border: 4px solid!important;
    color: #ea1d07!important;
  }

  :not(textarea):not(input):not([contenteditable])
  {
    text-shadow: 0 0.05em rgba(0,0,0,.5),0 -0.05em rgba(0,0,0,.5),0.05em 0 rgba(0,0,0,.5),-0.05em 0 rgba(0,0,0,.5)!important;
  }

  :not([class*="video-extras-sparkbar"]):not([class^="ytp-"]):not(.sidebar):not(.video-time):not([class^="html5-"]):not(.yt-uix-button-primary):not(label):not(.toggle):not(.branding-context-container-inner):not(.iv-drawer)
  {
    background-color: transparent!important;
    color: variable!important;
  }

paper-toggle-button[checked]:not([disabled]) .toggle-button.paper-toggle-button 
  {
    color: #ea1d07!important;
  }
  
html:not(.style-scope) 
   {
    --primary-text-color: #ffffff;
    --primary-background-color: #fff02b;
    --disabled-text-color: #9b9b9b;
    --divider-color: #dbdbdb;
    --error-color: #dd2c00;
    --primary-color: #3f51b5;
    --accent-color: #ff4081;
    --text-primary-color: #ffffff;
     }
    


[role="alert"],.content-region,.yt-uix-checkbox-on-off .checked:after
  {
    display: block!important;
    background-color: white
  }

  :not(#​) :link
  {
    color:  #99CCFF!important;
  }

  :not(#​) :visited
  {
    color: #CC99FF!important;
  }

  :not(#​) select
  {
    background: #ea1110!important;
  }

 [href="/"][id*="logo"],:not(.ytp-volume-slider-handle):not(.yt-uix-playlistlike):before,.yt-uix-button-icon-material-upload,.yt-uix-button-icon-bell,.yt-uix-button-icon-wrapper>.yt-sprite,.autoplay-info-icon,.autoplay-button,.search-button>.yt-uix-button-content,#appbar-guide-button,.toggle-button.paper-toggle-button,ytd-toggle-button-renderer.force-icon-button a.ytd-toggle-button-renderer
  {
    -webkit-filter: invert(1)hue-rotate(180deg);
    filter: invert(1)hue-rotate(180deg);
    color: #ea1d07!important;
  }

    [class$="container"],#yt-comments-paginator,.style-scope ytd-shelf-renderer,.style-scope ytd-notification-topbar-button-renderer,.menu-container style-scope ytd-multi-page-menu-renderer,.style-scope ytd-shelf-renderer,.ytd-multi-page-menu-renderer[menu-style="multi-page-menu-style-type-comments"] > .menu-container.ytd-multi-page-menu-renderer:not(:hover), ytd-multi-page-menu-renderer[menu-style="multi-page-menu-style-type-conversations"] > .menu-container.ytd-multi-page-menu-renderer:not(:hover), ytd-multi-page-menu-renderer[menu-style="multi-page-menu-style-type-notifications"] > .menu-container.ytd-multi-page-menu-renderer:not(:hover)
  {
    color: #ea1d07!important;
  }
  
 [class="menu-container.ytd-multi-page-menu-renderer"]
  {
    background-color : #fff ;
    background : var(--yt-spec-brand-background-solid);
    overflow: auto;
    display: inline-block;
  }
  
   [class="style-scope yt-multi-page-menu-section-renderer"]
  {
    border: 5px solid!important;
    border-color: #ea1d07!important;
  }
  
 [href="/"][id*="logo"],.yt-uix-button-shelf-slider-pager,.yt-uix-form-input-select,.yt-uix-button-icon-wrapper,.yt-text,ytd-multi-page-menu-renderer,ytd-multi-page-menu-renderer[menu-style="multi-page-menu-style-type-conversations"] #sections.ytd-multi-page-menu-renderer > *.ytd-multi-page-menu-renderer
  {
    -webkit-filter: invert(1)hue-rotate(180deg);
    filter: invert(1)hue-rotate(180deg);
    color: #ea1d07!important;
  }
  
  [class$="container"],#yt-comments
  {
    background-color: #a5a5a5!important;
    display: block;
  }

  :not(h2):not(button):not(.comment-simplebox-renderer-collapsed-content),#footer-container button,.yt-uix-button-shelf-slider-pager
  {
    border: 10px!important;
  }

  textarea,[contenteditable="true"],[type="text"]:not(#masthead-search-term),[role="menu"]:not(.guide-user-links):not(.ytp-panel-menu),#footer-container .yt-uix-button-default,.yt-uix-clickcard-card-visible,#watch-appbar-playlist,.comment-simplebox-frame,body>table
  {
    box-shadow: inset 5px 5px 5px 5px rgba(255,255,255,.5)!important;
    color: #ffffff
  }

  #masthead-search-terms
  {
    box-shadow: none!important;
  }
}

@-moz-document regexp("https://apis.google.com/u/0/_/hovercard.*")
{
  *
  {
    background-color: #000!important;
    color: #fff!important;
    border-color: #000!important;
  }
}
