css:
@-moz-document domain("upwork.com") {
  body[data-ng-app="JobSearchUI"] .air-card > jsui-tabs:first-child,
  body[data-ng-app="JobSearchUI"] .air-card > header > div.row:nth-last-child(2),
  .job-tile:first-child,
  .job-tile > div > div > div > .row > .col-md-2,
  .job-tile > div > div > div > .row > .col-md-10 > div.m-sm-top {
    display: none;
  }

  .job-tile > div > div > div > .row > .col-md-10 {
    -ms-flex: 0 0 100%;
    flex: 0 0 100%;
    max-width: 100%;
  }

  .job-tile > div > div > div > .row > .col-md-10 > div {
    display: inline-block;
  }

  .job-tile > div > div > div > .row > .col-md-10 > small:nth-last-child(2) {
    display: block;
    padding-top: 20px;
  }
}
