css:
@-moz-document url-prefix("https://forums.spacebattles.com/threads/") {
.messageUserInfo {
    float: right !important;
    padding-left: 25px;
}
.messageUserBlock .arrow {
    transform: rotate(180deg);
    left: -10px;
}
.message .messageInfo {
    margin-left: inherit !important;
}
.message .privateControls {
    float: right;
}
.message .publicControls {
    float: left;
}
article {
    text-align: justify;
}
.message .messageContent {
    display: inline;
}
#QuickReply {
    margin-left: inherit !important;
}
.quickReply > .messageUserInfo {
    display: none;
}
.pageWidth {
    max-width: 48em !important;
}
}
