css:
@-moz-document url-prefix(http://my.ya.ru) {
td.b-page-body {
    width:100% !important;
}
td.b-page-right {
    overflow: hidden;
}
td.b-page-right .b-lenta-filter {
    width: 14em;
    margin-right: -15em !important;
    border: 1px solid #dcdcdc;
    border-width: 0 0 1px 1px;
    z-index: 300 !important;
}
td.b-page-right .b-lenta-filter:first-child {
    border-top-width: 1px;
}
td.b-page-right .b-lenta-filter + .b-lenta-filter {
    margin-top: 0 !important;
}

td.b-page-right:hover {
    overflow: visible;
}
td.b-page-right:hover .b-lenta-filter {
    width: 14em;
    margin-left: -15em !important;
    margin-right: 0 !important;
}
}
