@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lokalisten.de") {

 body {background:none red!important; padding:0%!important}

 .cityLogo,.skyScraper,.sponsor,.logo,.bx_home .notifyBox+.fixedBox,#l_subFooter,#BANNER_popup,#BANNER_SOIWPFB2,#BANNER_fullbanner,#BANNER_rectangle {display:none!important;}

 #friendSelector {color:black!important; background-color:white!important;}

 #l_header, div#l_navColumn, p.inviteFriends, div#l_columnInfo, div#hdrSearchfield, div.closed div#l_leftColumn, #l_navigation {border-width:0px!important;}



 .inviteFriends, #l_navColumn, .userInfo, #l_columnInfo, #l_subHeader, #l_division, #l_navigation, #l_headerTop, #tecMenu, #hdrSearchfield {background-image:none!important; background-color:#444!important; top:0px!important;}

 .eck,#l_main,#l_outer,#l_inner1,#l_inner2,#l_footer {background:none!important;}

 #l_headerTop {height:12px!important; padding:3px 0 4px!important;}

 #l_header, #l_headerTop, #tecMenu {background:transparent none!important;}

 #l_wrapper, .message {margin:0px!important;}

 #l_outer {width:846px!important;}

 #l_content, #l_basicData {margin:0px!important; padding:0px!important; margin-left:179px!important;}

 #l_leftColumn {width:179px!important;}

 #bx_container {width:650px!important;}

 #l_innerContent, #bx_home, #bx_home_inner {width:657px!important;}

 #l_basicData, #l_basicData * {color:white!important; background-image:none!important; background-color:#444!important; border:0px!important;}

 #tinymce, .charmap a {color:white!important;}

 #l_navColumn li>* {padding:0px 0px 2px 0px!important; line-height:11px!important;}

}
