css:
@-moz-document domain("7cups.com") {
/* by shade */
 body {
    background: #fff !important;
    color: #fff !important;
}
.navbar {
    background-image: none !important;
}
body, .container-fluid, .hidden-sm-down {
    background-color: #17191A !important;
}
#bottomStatus, .bottomStatus {
    color: #fff !important;
    background-color: #2B2F30 !important;
}
#bottomStatus.heavyLoad {
    color: #fff !important;
    background-color: #2B2F30 !important;
    background: #089FD1 !important;
}
.convHolder, #actionButtons, #actionButtons img {
    background-color: #FFF !important;
}
#bottomStatus .btn-primary {
    border: none !important;
}
.btn-default, .btn-primary {
    color: #fff !important;
    background-color: #17191A !important;
}
.card .nav-link, .active {
    color: #fff !important;
}
.card {
    background-color: #2B2F30 !important;
}
.card .nav-link, .active {
}
.convHolder, #actionButtons, #actionButtons img {
    background-color: #2B2F30 !important;
}
.convHolder .note {
    color: #FFFFFF !important;
}
.card-title {
    color: #ffffff !important;
}
textarea {
    background-color: #2B2F30 !important;
    color: #fff !important;
}
.meWrap {
    display: inline-block;
    color: black;
    background-color: #1e90fe;
    border-top-left-radius: .5em;
}
.youWrap {
    display: inline-block;
    font-size: 1.1em;
    color: #0d1010;
    background-color: #e0f3ff;
    border-bottom-right-radius: .5em;
}
.text-center, .details {
    color: #fff !important;
}
#MessageScrollDivResize, #MessageScrollDiv {
    border: none !important;
}
.modal-content {
    background-color: #2B2F30 !important;
}
.modal-content a {
    color: #fff !important;
}
.modal-title {
    color: #fff !important;
    background: inherit !important;
}
.listenerData {
    color: #fff !important;
}
table .listenerData .data {
    color: #fff !important;
}
.userScreenName {
    color: #f0f8ff
}
/* by isa */
 .compassionHeart {
    color: #42C0FB;
}
.blueStars span {
    display: inline-block;
    width: 100%;
    height: 100%;
    background-image: url(http://s32.postimg.org/rwi1skddx/rating_stars_empty.png;);
    background-position: 0 50%;
}
.img-circle {
    border-radius: 50% !important;
}
.img-fluid {
    border-radius: 50% !important;
}
.youWrap:hover {
    box-shadow: 0 0 0 .2625em blue;
    color: darkblue;
}
a, h4 {
    color: lightblue;
}
a.btn.btn-white {
    background-color: #8a90a3 !important;
    color: #fff !important;
}
.activeConv {
    background-color: #1e90fe !important;
}
alert-info {
    background: #000;
}
.col-md-6.col-lg-7 {
    color: #fff;
}
div.container.text-center {
    background: #000 !important;
}
#layout-wrapper > div:nth-child(7) {
    background: #000 !important;
}
#roiC {
    padding: 13px !important;
}
#layout-wrapper > div:nth-child(5) {
    background: #000 !important;
}
#layout-wrapper > div:nth-child(3) > div {
    background: #000 !important;
}
#layout-wrapper > div:nth-child(3) {
    background: #000 !important;
}
#layout-wrapper > div:nth-child(10) > div:nth-child(4) > div > div {
    color: white;
}
.card table {
    color: #fff !important;
    ;
    background: #373a3c !important;
    border: none !important;
}
.table-upgrade {
    background-color: #8a90a3;
    border-radius: .8em;
}
.table-bordered, .table-bordered td, .table-bordered th {
    border: none;
}
.postMessageCopy {
    color: #fff;
}
.forumPostHead {
    color: #999;
}
#layout-wrapper > div.text-center {
    background: none !important;
}
#form1 > label {
    color: #fff !important;
}
}
