@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("docs.google.com") {

	/* documents list hover */
	tr.doclist-tr:hover{background-color:#ffeb86 !important;}
	tr.doclist-tr td{border-bottom:1px solid #ccc !important;}
	/**/

	/* border & date separator */
	div.doclistview-inner,tr.doclist-header td{border-color:#ddd !important;}
	td.doclist-dateseparator{color:#f66 !important;}
	/**/

}
