css:
@-moz-document domain("dle.rae.es") {
body {
    background-color: #333;
}

#diccionario,
#main > section,
#contenidos,
#ctsecond,
#drae23 {
    background-color: #333;
}

.tweets {
    display: none;
}

#irae,
#idle,
#iasale,
#enclave > a > img,
#enclave > hr {
    display: none;
}

#enclave > span > a,
#palabraD > p.words > a,
a {
    color: lightblue;
}

a:visited {
    color: rgb(125, 75, 150);
}

a:hover {
    color: lightgreen;
}

.osc,
.menu,
#palabraD > p:nth-child(3) > span,
#a0 > p {
    color: #ddd;
}

#enclave {
    display: flex;
    align-items: center;
    justify-items: center;
    align-self: center;
}

#informa > div.bloqueIn.DE > img,
#informa > div.bloqueIn.dledes > img,
#informa > div.bloqueIn.dejdes > a:nth-child(3) > img,
#informa > div.bloqueIn.bcrae > a:nth-child(3) > img,
#informa > div.bloqueIn.promo > a:nth-child(3) > img,
#informa > div.bloqueIn.qx > a:nth-child(3) > img {
    display: none;
}

#resultados,
#formList,
#wq {
    -webkit-filter: invert(80%);
    filter: invert(80%);
}

#drae23 {
    display: flex;
    align-items: center;
    justify-content: center;
    align-self: center;
    margin: 10px;
}

#ctsecond *,
.menu *,
#ctsecond ul.menu li a,
#ctsecond ul.menu li:before {
    color: #ddd;
    border-color: transparent #ddd;
    border-top-color: transparent;
    border-right-color: #ddd;
    border-bottom-color: transparent;
    border-left-color: #ddd;
}
}
