css:
@-moz-document domain(dropmail.me) {




body {
    color: #b3a9a9;
    background-color: #535558;
}

.well {
    background-color: #3e4044;
    border: 1px solid #b3a9a9;

}

.alert-info {
    color: #5bacd4;
    background-color: #556065;
    border-color: #b3a9a9;
}

.btn-default {
    color: #b3a9a9;

}

code {
    color: #fd3c6d;
    background-color: #382b2f;

}


.dropdown-menu {
    background-color: #929394;

}

pre {
    color: #eae9e9;
    background-color: #6d7179;
    border: 1px solid #b3a9a9;

}


a:focus, a:hover {
    color: #c1e2ff;

}

.label {
    color: #464646;

}

.btn-default.active.focus, .btn-default.active:focus, .btn-default.active:hover, .btn-default:active.focus, .btn-default:active:focus, .btn-default:active:hover, .open>.dropdown-toggle.btn-default.focus, .open>.dropdown-toggle.btn-default:focus, .open>.dropdown-toggle.btn-default:hover {
    background-color: #929394;
    border-color: #b3a9a9;
}

.btn-default {
    background-image: linear-gradient(to bottom, #4c5465, #3e4044);
    
}

.btn-default:hover {
    color: #d6caca;

}

.dropdown-menu>li>a:focus, .dropdown-menu>li>a:hover {
    color: #b1b1b1;
    background-color: #3e4044;
}

.dropdown-menu .divider {
    background-color: #3e4044;
}

.btn-default.active, .btn-default:active, .open>.dropdown-toggle.btn-default {
    color: #b3a9a9;
    background-color: #565252;
    border-color: #b3a9a9;
}

a {
    color: #7590a7;
}

.locale-links .highlighted-locale {
    box-shadow: 0 0 1em #535558;
    background-color: #2a34425e;
}

.progress {
    background-color: #556065;

}


}
