css:
@-moz-document domain("bikroy.com") {
body {
    overflow-x: hidden;
}

[class^="main-section--"] {
    max-width: 1200px;
}
[class^="main-section--"] > div:first-child {
    display: none;
}

[class^="list-wrapper"] {
    margin-right: 0;
}
[class^="list-wrapper"] + div {
    display: none;
}

[class^="page-details"] {
    display: none;
}

[class^="ad-list-container"] {
    max-width: none;
    margin: 0;
    padding: 25px;
}

ul[class^="list"] {
    display: flex;
    flex-wrap: wrap;
    width: 100%;
    border-left: 1px solid #d4ded9;
}
ul[class^="list"] > li {
    width: 50%;
    border-right: 1px solid #d4ded9;
}
ul[class^="list"] > li:nth-last-child(2) {
    margin-bottom: -1px;
}
ul[class^="list"] > li:nth-last-child(1),
ul[class^="list"] > li:nth-last-child(2) {
    border-bottom: 1px solid #d4ded9;
}

[class^="top-ads-container"] {
    display: none;
}

[class^="membership-container"] {
    position: static;
}
[class^="membership-container"]:before {
    content: " ";
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: #fff;
}
[class^="membership-container"] > div {
    display: none;
}

[class^="lowerboard"] {
    display: none;
}

[class^="footer-container--"] {
    display: none;
}

}
