.select_metro__button {background-color: #222; border-color: rgba(255,255,255,.2);}
.select_metro__button:hover {border-color: rgba(255,255,255,.3);}
.select_metro._pressed .select_metro__button {background-color: #333;}
.select_metro__text {color: #eee;}
.select_metro__tick {filter: invert(1);}

.y-popup_islet {background-color: #222; box-shadow: 0 0 0 1px rgba(255,255,255,.1), 0 10px 20px -5px rgba(0,0,0,.4);}
.select-item_metro {color: #eee;}
.select-item_metro-plain:hover, .select-item_metro:hover {background-color: #444;}
.select-item_metro._selected .select-item_metro__check {filter: invert(1);}
.station-label._highlighted, .station-label:hover {background-color: #444;}

.header_islet__board-call-button {border-color: #444;}
.header_islet__board-call-button:hover {border-color: rgba(255,255,255,.3);}
.header_islet__board-call-icon {background-image: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" width="32" height="32"><path fill="%23eee" d="M3 12h8V6H3v6zm9 0h8V6h-8v6zm9-6v6h8V6h-8zM3 19h8v-6H3v6zm9 0h8v-6h-8v6zm9 0h8v-6h-8v6zm0 7h8v-6h-8v6zm-9 0h8v-6h-8v6zm-9 0h8v-6H3v6z"/></svg>');}
.header_islet._board-visible .header_islet__board-call-button, .header_islet__board-call-button:active {border-color: rgba(255,255,255,.3);}

.header_islet__services-board {background: #222; border-color: #444;}

/* Left Panel */
.sidebar__box, .sidebar__box:last-child {background-color: #222; border-color: #444; color: rgba(255,255,255,.8);}
.sidebar__box-inner-separator {border-top-color: rgba(255,255,255,.1);}

.scrollable-block__bar:after {background-color: rgba(255,255,255,.15);}
.scrollable-block._bar-pressed .scrollable-block__bar:after, .scrollable-block__bar:hover.scrollable-block__bar:after {background-color: rgba(255,255,255,.5);}

.y-input_islet {background-color: #222;}
.y-input_islet, .y-popup_islet__arrow {border-color: rgba(255,255,255,.2);}
.y-input_islet__control {background-color: #222; color: #eee;}
.y-input_islet__clear {filter: invert(1);}
.y-input_islet._disabled {background-color: rgba(255,255,255,.08); border-color: rgba(255,255,255,.08);}

.route-list-item {border-top-color: rgba(255,255,255,.1);}
.route-list-item._selected {background-color: rgba(255,204,0,.3);}
.route-list-item__transfers {color: #7ad;}
.route-details-block__hint {color: #e85253;}
.route-details-block__icon-bottom-arc, .route-details-block__icon-top-arc, .route-details-block__icon-two-bars {background: none; border-color: #eee;}
.route-details-block__icon-walk {filter: invert(1);}
.route-details-block__icon-circle, .route-details-block__icon-circle-white-stroke {border-color: rgba(255,255,255,.5);}
.route-details-block__icon-circle-white-stroke {border-color: #222;}
.route-details-block__icon-bar, .route-details-block__icon-bar-avg, .route-details-block__icon-bar-long {opacity: .8;}

