.permalink-container__text {color: #7ad;}
.permalink-container__text:hover {color: #e85253;}

/* Map */
.ymaps-2-1-74-map {color: #eee;}

.zoom-control__minus, .zoom-control__plus {background-color: #222; border-color: rgba(255,255,255,.2);}
.zoom-control__minus:hover, .zoom-control__plus:hover {border-color: rgba(255,255,255,.3);}
.zoom-control._minus-pressed .zoom-control__minus, .zoom-control._plus-pressed .zoom-control__plus {background-color: #333;}
.zoom-control._disabled .zoom-control__minus, .zoom-control._disabled .zoom-control__plus, .zoom-control._minus-disabled .zoom-control__minus, .zoom-control._plus-disabled .zoom-control__plus {background-color: #333; border-color: rgba(255,255,255,.1);}
.zoom-control__plus {background-image: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" width="26" height="26"><path d="M11 15H6v-4h5V6h4v5h5v4h-5v5h-4v-5zm0 0" fill="%23aaa"/></svg>');}
.zoom-control__minus {background-image: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" width="26" height="26"><path d="M6 11h14v4H6z" fill="%23aaa"/></svg>');}

#scheme-layer-background rect {fill: #222;}
#scheme-layer-labels rect[fill="#fff"] {fill: #222;}
#scheme-layer-labels text {fill: #eee;}

#scheme-layer-links {opacity: .8;}
#scheme-layer-transfers {fill: rgba(34,34,34,.6); stroke: #eee;}
#scheme-layer-stations {stroke: #eee;}

#highlight-layer-links {opacity: .8;}
#highlight-layer-transfers {fill: rgba(34,34,34,.6); stroke: #eee;}
#highlight-layer-stations {stroke: #eee;}
.scheme-view #highlight-layer-labels .selected-label rect, .scheme-view #scheme-layer-labels .selected-label rect {stroke: #666;}
#highlight-layer-labels rect[fill="#fff"] {fill: #222;}
#highlight-layer-labels text {fill: #eee;}

.ymaps-2-1-74-copyright__content {background: rgba(34,34,34,.75);}
.ymaps-2-1-74-copyright__link {color: #eee !important;}
.ymaps-2-1-74-copyright__link:hover {color: #e85253 !important;}

