css:
@-moz-document domain("hobbyconsolas.com") {
body,p,span,.views-element-container a {
    color:  #D3D3D3 !important;
}

h1,h2,h2 a,h3,h3 a,h4 a,a.icon-hc-comments,li{
    color:  white !important;
}

input,select,option,textarea{
    color: white !important;
    background-color: #444 !important;
    border: 1px solid gray;
}

/*Boton escribir comentario*/
#comments_anchor .icon-hc-comments{
    color: white !important;
}
/*v1.3 Enviar comentario*/
.comment-form > div > div > div div
{
    background-color: #333333 !important;
    color:white !important;
}

body,header,article,main,section,
#footer,#footer *,
h3.sunmedia-title,li.active > a,.tabs .tab-links > li.active::after,.block-newsletter,.create-review{
    background-color: #333333 !important;
}

/*v1.2 Navbar semitransparente*/
.layout_header-top
{
    background-color: #000000d9 !important;
}

img[src='/themes/hobbyconsolas/img/logo-hc-new-p.svg']{
    filter:invert(100%);
}

.article-featured-header-vertical span{
    background: rgba(0,0,0,0.55) !important;
}
  
  /*v1.4 Ocultar mensaje de encuesta*/
.smcx-widget{
    display:none;
}
}
