css:
/* turn ON visited links highlighting */
@-moz-document
 url-prefix(http://)
,url-prefix(https://)
,url-prefix(file://)
,url-prefix(chm:file://)
{
i {font-style: italic !important; }
/* */
a {border-bottom: 0.05px dotted rgba(47,95,95,.5) !important;}
a:visited {border-bottom: 0.05px solid rgba(127,0,0,1) !important;}
a:visited{ color: #A40 !important; opacity: 0.9 !important;}
a:visited:hover { color: #520 !important;}
}
