css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("service.gmx.net")
{

#mboxSearchForm
{
display:block !important;
border-width:0 !important;
position:relative !important;
top:0 !important;
}

#mboxSearchForm form
{
float:right !important;
}

#mboxSearchForm form h2.form
{
font-size:smaller !important;
display:inline !important;
padding-right:1em !important;
display:none !important;
}

#mboxSearchForm form:before
{
content:'Mailsuche' !important;
background-image:url(http://images.gmx.net/images/gmx/mail/search/search.gif);
background-repeat:no-repeat;
padding-right:0.5em !important;
padding-left:25px !important;
padding-top:5px !important;
padding-bottom:5px !important;
vertical-align:middle !important;
font-weight:bold;
color:#244E7E;
}

#mboxSearchDiv
{
display:none !important;
}

div#mboxSearchForm + script + p
{
float:right !important;
}

}
