css:
@-moz-document regexp("https?://.*\\.bing\\.com/((\\?.*?)?)$") {
#hp_scroll,
#hp_container .hpcLogoWhite {
    display: none !important;
}
#sbox {
    top: 45%;
}
#est_switch {
    position: unset !important;
    margin-left: unset !important;
    margin-top: unset !important;
}
}

@-moz-document regexp("https?://.*\\.bing\\.com/search.*$") {
/* 隐藏元素 */
#ev_talkbox_min,
#b_opalpers {
    display: none !important;
}
/* 基础样式 */
h2 {
    line-height: 24px;
    font-size: 20px;
}
em,
strong {
    font-style: normal;
    color: #c00 !important;
}
a,
#b_tween a:visited,
#b_results .b_no a {
    color: rgb(0, 0, 204);
}
/* 搜索结果页标题栏修改 */
#b_header {
    padding: 12px 0 0 0;
    border-bottom: 0px;
}
.b_scopebar {
    margin: 2px 0 0 0;
    padding: 0 0 0 100px;
    background: #f8f8f8 !important;
    border-top: 1px solid #ddd;
}
.trans_button_group {
    top: 53px;
}
.b_scopebar .b_active {
    border-bottom: 3px solid #38f;
}
.trans_button {
    background-color: #e4e4e4;
}
.b_attribution,
#b_content .b_attribution {
    font-size: 13px;
    line-height: 20px;
}
#b_results > li a {
    color: rgb(0, 0, 204);
}
/* 搜索结果二级标题 */
.deeplink_title > a {
    font-size: 15px;
}
#b_results .b_algo .b_vlist2col.b_deep li {
    padding: 0 0 5px;
}
#b_context,
.rc_rnDesktop_vlHorizontalImage,
.tipContainer,
.bottomTipContainer {
    display: none;
}
#b_tween {
    margin-top: 1px;
}
#b_content {
    padding: 0;
    width: 1260px;
    margin: 0 auto;
}
#b_results {
    width: unset !important;
}
#b_results > li {
    display: inline-block !important;
    width: 50% !important;
    -moz-box-sizing: border-box !important;
    box-sizing: border-box !important;
    padding: 10px !important;
    border-top: 2px dotted #ddd;
    background-clip: padding-box !important;
    margin: 0 !important;
    vertical-align: top !important;
    counter-increment: gOrder 1;
}
#b_results > li.b_ans.b_topborder, #b_results > li.b_ans.b_topborder.b_tophb.b_topshad {
    box-shadow: unset;
    border-top: 2px dotted #ddd;
}
}

@-moz-document regexp("https?://.*\\.bing\\.com/(images|videos|academic|dict)/search.*$") {
/* 隐藏元素 */
#ev_talkbox_min,
#b_opalpers {
    display: none !important;
}
/* 搜索结果页标题栏修改 */
#b_header {
    padding: 12px 0 0 0;
    border-bottom: 0px;
}
.b_scopebar {
    margin: 2px 0 0 0;
    padding: 0 0 0 100px;
    background: #f8f8f8 !important;
    border-top: 1px solid #ddd;
}
.b_scopebar ul {
    margin-left: unset;
}
.trans_button_group {
    top: 53px;
}
.b_scopebar .b_active {
    border-bottom: 3px solid #38f;
}
#fltIdt {
    position: relative;
    top: 9px;
}
}
