css:
@document url("http://www.index.hr/"),
          url-prefix("http://index.hr"),
          domain("index.hr"),
          regexp("https:.*")
{
body
{
    color: #e2e2e2;
    background-color: #202020;
}

header
{
    background-color: #202020;
}

footer
{
    background-color: #202020;
}

.breadcrumbs
{
    background-color: #202020;
}

.main-content-bg
{
    background: #202020;
}

.main-category-holder a
{
    color: #fff;
}

.timeline-content ul li .title-box a
{
    color: #fff;
}

.secondary-content-bg
{
    background: #cd5c5c
}

.timeline-content ul li .side-content
{
    background: #202020;
}

.home-key-info-container .key-info-holder
{
    background: #202020;
}

.home-key-info-container .key-info-holder ul li .key-info
{
    color: #fff;
}

.main-menu-container .main-menu .menu-holder
{
    border-bottom: 1px solid #151515;
    border-top: 1px solid #151515;
    background: #202020;
}

.main-menu-container .main-menu li a,
.main-menu-container .main-menu li .expandable
{
    color: #dedede;
}

.plus-categories .cube-plus-holder a
{
    color: #fff;
}

.cube-holder-oglasi a
{
    color: #fff;
}

.article-read-more-holder a
{
    color: #e2e2e2;
}

.main-category-header .main-category-navigation ul li.root-category
{
    background: #202020;
}

.grid-items a
{
    color: #e2e2e2;
}

.cube-big-holder a
{
    color: #f1f0f0;
}

.main-menu-container .main-menu .dropdown-content
{
    background: #202020;
}

.related-articles-holder a
{
    color: #e2e2e2;
}

.category-timeline-holder .header .periods .dropdown-toggle
{
    color: #e2e2e2;
}

.category-timeline-holder .header h2
{
    color: #efecff;
}

.open > .dropdown-menu
{
    background-color: dimgrey;
}

.main-footer .list-holder ul li.heading a
{
    color: #fff;
}

.currency-container .left-part .conversion-holder .input-holder .input-field
{
    color: #fff;
    background-color: #202020;
}

.currency-container .left-part .conversion-holder .input-holder .select-arrow-style select
{
    border-color: #fff;
    background: #202020;
}

.currency-container .left-part .exchange-rates-holder table tbody tr.primary
{
    background: #202020;
}

.cinema-container nav .day,
a
{
    color: #e2e2e2;
}

.cinema-container .select-arrow-style select
{

    background: #1d1d1d;
}

.cinema-container .city-holder select option
{
    background: #202020;
    color: #e2e2e2;
}

.main-timeline .main-timeline-holder .category-tags .tag-item
{
    color: #e2e2e2;
}

.main-timeline .main-timeline-holder .timeline-content ul li .title-box .content .summary
{
    color: #e2e2e2;
}

.drop-down-filter .timeline .dropdown-toggle
{
    color: #e2e2e2;
}

.main-timeline .main-timeline-holder .timeline-tab li a
{
    color: #3d3d3d;
}

.main-timeline .main-timeline-holder .timeline-tab li.active a
{
    color: #dfdfdf;
}

.tags-holder .tag-item
{
    background: #424242;
}

.tags-holder .tag-item:hover
{
    background: #eb1e25;
}

.sub-menu-holder .sub-menu .sub-menu-item:hover
{
    color: #eb1e25;
}

.sub-menu-holder .sub-menu .sub-menu-item:focus
{
    color: #8c1216;
    ;
}

.grid-items-list .grid-item .content
{
    color: #dedede;
}

.grid-items-list .grid-item .content .summary
{
    color: #dedede;
}

.grid-items-list .grid-item.featured a
{
    background: #cd5c5c;
}

.features_list
{
    background: #232323;
}

.gallery,
.gallery_large
{
    background: #202020;
}

.oglas_functions ul li a
{
    background: #202020;
}

.similarOglasiContent
{
    background-color: #202020;
}

header .header_myAccount a
{
    color: #fff;
}

footer .links ul li a
{
    color: #fff;
}

.result
{
    color: #e2e2e2;
    background: #202020;
}

.result .result_body .title
{
    color: #fff;
}

ul.side-nav
{
    background-color: #202020;
}

ul.side-nav .collapsible-header
{
    color: #fff;
}
}
