css:
@-moz-document domain("twitch.tv") {
body {
    font-family: Helvetica, sans-serif !important;
}

.tw-font-size-1,
.tw-font-size-2,
.tw-font-size-3,
.tw-font-size-4,
.tw-font-size-5,
.tw-font-size-6,
.tw-font-size-7,
.tw-font-size-8,
.tw-font-size-9,
.tw-font-size-10,
button,
a,
h,
h1,
h2,
h3,
h4,
h5,
span,
.tw-strong,
.tw-c-text-alt-2,
.tw-c-text-alt {
    font-weight: 100 !important;
    font-family: Helvetica, sans-serif !important;
}


.tw-root--theme-dark body {
    background-color: #0f0f0f;
    --color-background-body: #0f0f0f;
    --color-background-alt: #0f0f0f;
}

.channel-header .tw-align-items-center {
    justify-content: flex-start !important;
}


.tw-root--theme-dark .directory-header-new__banner-cover {
    background: linear-gradient(0deg, #1b1a1a, rgba(0, 0, 0, .25)), linear-gradient(90deg, #1b1a1a, rgba(0, 0, 0, .25));
}
.tw-root--theme-dark .channel-root__right-column {
    background: #1b1a1a;
}

.tw-root--theme-dark .chat-room {
    background: #1b1a1a;
}

.channel-header__right button {
    background-color: #1d1d1d !important; 
}

.tw-font-size-5 {
    font-size: 1.4rem!important;
}

.tw-font-size-4 {
    font-size: 1.4rem!important;
}

.tw-flex-grow-1.tw-flex-shrink-1.tw-overflow-hidden.tw-pd-r-2 h2{
    font-size: 1.5rem!important;
}

.tw-textarea:focus {
    background: #0f0f0f;
}
.tw-root--theme-dark {
    --color-background-base: #0f0f0f;
}

.tw-animated-glitch-logo__body {
    fill: #6441A4;
}

.tw-svg__asset path {
    fill: #6441A4;
}

.chat-line__message:nth-child(odd),
.chat-line__moderation:nth-child(odd),
.chat-line__status:nth-child(odd) {
    background-color: rgba(50, 50, 50, .1);
}


.tw-align-items-center.tw-flex.tw-flex-nowrap.tw-full-height.tw-full-width.tw-justify-content-center.tw-pd-x-1 li:nth-child(1) {
    Display: none !important;
}

.tw-root--theme-dark .side-nav__scrollable_content {
    background-color: #0f0f0f !important;
}


}
