@-moz-document url(https://web.whatsapp.com/) {
:root {
    --accent: /*[[accent]]*/;
    --image: url('/*[[image]]*/');
    --app-background-stripe: transparent;
    --background-default: rgba(0, 0, 0, 0.25);
    --background-default-active: rgba(255, 255, 255, 0.2);
    --background-default-hover: rgba(255, 255, 255, 0.1);
    --border: rgba(255, 255, 255, 0.04);
    --border-chatlist: var(--border-stronger);
    --border-panel: transparent;
    --border-strong: rgba(255, 255, 255, 0.06);
    --border-stronger: rgba(255, 255, 255, 0.08);
    --bubble-meta: var(--secondary);
    --bubble-meta-icon: var(--secondary);
    --button-primary-background: var(--accent);
    --button-secondary: #fff;
    --button-secondary-hover: var(--accent);
    --chatlist-icon: var(--secondary);
    --compose-primary: var(--message-primary);
    --conversation-panel-background: transparent;
    --compose-background: var(--input-field);
    --compose-panel-background: transparent;
    --compose-input-background: var(--intro-background);
    --compose-input-border: var(--intro-background);
    --dark: #000;
    --drawer-background: var(--background-default);
    --drawer-gallery-background: var(--background-default);
    --drawer-section-background: var(--background-default);
    --dropdown-background: #000;
    --icon-ack: var(--accent);
    --icon-lighter: #777;
    --incoming-background: #444;
    --incoming-background-deeper: rgba(0, 0, 0, 0.3);
    --incoming-background-rgb: rgb(68, 68, 68);
    --input-field: rgba(255, 255, 255, 0.2);
    --intro-background: rgba(0, 0, 0, 0.8);
    --media-viewer-background: #000;
    --media-viewer-button-background: #000;
    --media-viewer-background-rgb: 0, 0, 0;
    --message-primary: #fff;
    --modal-backdrop: var(--intro-background);
    --modal-background: #000;
    --notification-e2e-background: var(--intro-background);
    --notification-text: var(--primary);
    --outgoing-background: #222;
    --outgoing-background-deeper: rgba(0, 0, 0, 0.3);
    --outgoing-background-rgb: rgb(32, 32, 32);
	--panel-background: rgb(30, 30, 30);
    --panel-background-colored: var(--accent);
	--panel-background-lighter: rgba(0, 0, 0, 0.5);
    --panel-background-deep: rgb(15, 15, 15);
    --panel-background-deeper: rgb(0, 0, 0);
    --panel-header-background: var(--background-default);
    --primary: #fff;
    --primary-strong: #fff;
    --primary-stronger: #fff;
    --primary-title: #fff;
    --rich-text-panel-background: transparent;
    --search-container-background: var(--background-default);
    --search-input-background: var(--intro-background);
    --secondary-lighter: #999;
    --secondary: #bbb;
    --secondary-stronger: #ddd;
    --system-message-background: var(--intro-background);
    --system-message-text: var(--primary);
    --teal: var(--accent);
    --unread-background: var(--intro-background);
    --unread-bar-background: var(--intro-background);
    --unread-marker-background: var(--accent);
}

/* Body reset */
body {
    font-family: font_name, 'Roboto', sans-serif;
    background-image: var(--image);
    background-size: cover;
    background-color: var(--dark);
}
.app-wrapper-web {
    background-image: none !important;
}
#startup, #initial_startup {
    background-color: var(--dark);
}
.spinner-path {
    stroke: var(--accent);
}
#progressbar {
    color: var(--accent);
    background-color: #fff;
}
.vW7d1 {
    padding-left: 4%;
    padding-right: 4%;
}
.h70RQ {
    background-color: transparent !important;
}
.app-wrapper-web .two > ._1xXdX, .app-wrapper-web .two > .Wu52Z, .app-wrapper-web .three > ._1xXdX, .app-wrapper-web .three > .Wu52Z {
    background-color: var(--panel-background-lighter) !important;
    border-radius: 5px;
}
._15G96 {
    color: var(--icon-lighter);
}
._1ypOz {
    background-color: var(--panel-background) !important;
}

/* Writing status... */
.vdXUe {
    color: var(--accent) !important;
}

/* Bottom Color Bar */
._2zynu:after {
    border-top: 6px solid var(--secondary) !important;
}

/* Modal */
._1CnF3 {
    color: #fff !important;
}

/* Message-Bubble Color */
.message-in .MVjBr, .message-in ._3e2jK {
    background-color: var(--incoming-background) !important;
}
.message-out .MVjBr, .message-out ._3e2jK {
    background-color: var(--outgoing-background) !important;
}
.message-in .tail-container {
    filter: brightness(0.26) grayscale(1);
}
.message-out .tail-container {
    filter: brightness(0.16) grayscale(1);
}

/* Remove Chat Background */
.YUoyu {
    display: none !important;
}

/* Input field */
._2MSJr, ._1Plpp {
    background-color: var(--input-field) !important;
    border: none !important;
}

/* Flex for .two */
.app-wrapper-web .two ._1xXdX {
    flex: 0 0 77px;
    transition: 0.5s flex-basis ease-in-out;
    margin-right: 5px;
}
.app-wrapper-web .two ._1xXdX ._itDl, .app-wrapper-web .two ._1xXdX ._2FBdJ {
    opacity: 0;
    transition: 0.5s opacity ease-in-out;
}
.app-wrapper-web .two ._3Bxar {
    overflow: visible;
}
.app-wrapper-web .two ._15G96 {
	left: -35px;
    position: relative;
    z-index: 100;
    color: var(--accent);
    transition: 0.5s left ease-in-out, 0.5s color ease-in-out;
}
.app-wrapper-web .two .Wu52Z {
    flex: 0 0 calc(100% - 82px);
    transition: 0.5s flex-basis ease-in-out;
}
.app-wrapper-web .two ._1xXdX:hover {
    flex: 0 0 25%;
}
.app-wrapper-web .two ._1xXdX:hover ._15G96 {
    left: 0;
}
.app-wrapper-web .two ._1xXdX:hover ._itDl, .app-wrapper-web .two ._1xXdX:hover ._2FBdJ {
    opacity: 1;
}
.app-wrapper-web .two ._1xXdX:hover ~ .Wu52Z {
    flex: 0 0 calc(75% - 5px);
}
._2MSJr {  /* Searchfield */
    opacity: 0;
    transition: 0.5s opacity ease-in-out;
}
.app-wrapper-web .two ._1xXdX:hover ._2MSJr {
    opacity: 1;
}
._20NlL {  /* Upper Menu */
    display: none;
}
.app-wrapper-web .two ._1xXdX:hover ._20NlL {
    display: inherit;
}

/* Flex for .three */
.app-wrapper-web .three ._1xXdX {
    flex: 0 0 77px;
    transition: 0.5s flex-basis ease-in-out;
    margin-right: 5px;
}
.app-wrapper-web .three .Wu52Z {
    flex: 0 0 calc(65%);
    transition: 0.5s flex-basis ease-in-out;
    margin-right: 5px;
}
.app-wrapper-web .three ._14VS3 {
    flex: 0 0 calc(35% - 87px);
    transition: 0.5s flex-basis ease-in-out;
}
.app-wrapper-web .three ._1xXdX:hover {
    flex: 0 0 25%;
}
.app-wrapper-web .three ._1xXdX:hover ~ .Wu52Z {
    flex: 0 0 45.5%;
}
.app-wrapper-web .three ._1xXdX:hover ~ ._14VS3 {
    flex: 0 0 calc(35% - 77px);
}
._2MSJr {  /* Searchfield */
    opacity: 0;
    transition: 0.5s opacity ease-in-out;
}
.app-wrapper-web .three ._1xXdX:hover ._2MSJr {
    opacity: 1;
}
._20NlL {  /* Upper Menu */
    display: none;
}
.app-wrapper-web .three ._1xXdX:hover ._20NlL {
    display: inherit;
}
}
