css:
@-moz-document domain("buymeacoffee.com") {
/*Dark Scrollbar*/
::-webkit-scrollbar { width: 10px; }
::-webkit-scrollbar-track { background: #fff0; }
::-webkit-scrollbar-thumb { background: #222; }
::-webkit-scrollbar-thumb:hover { background: #252525; }

body, html,
.body-color,

.or-text,

.intercom-1xafcqx,

.auth-wrapper,

/*building*/
.subdomainPost .postContainer,
.accountModal,

.box-bg-f2,

.creator-pg-bg-web
{
  background: #3d3d3f;
}

.supp-note,
.no-of-coffee,

.creator-pg-link, .featured-user,
.home-pg-ex-cat,
.tweet-wrapper,

.ctr-update-wrapper,

.intercom-home-screen-conversation-card,

.ex-creator-card, .ex-creator-tile, .ex-category,

.front-drop, .auth-option,

.b-shadow,

/*building*/
.boardDetails,
.boardHome .postListContainer,
.subdomainSidebarContainer .sidebarContainer .sidebar .sidebarSection,
.signupForm .topContainer .authButton, .signupForm .topContainer .emailButton,

.bmc-grey-input,

.active-option-wrapper, .custom-color-page .active-option, .p-l-30,
#support > div,

.bg-fff,

.dash-dd,

.extra-temp-each,

.mem-rew-each, .mem-rew-each input,

#canny-changelog-iframe,

/*New stuff?*/
.home-floating-wrapper,

.modal-content,

.pe-checkmark,

.pe-menubar,
.bmc-dropdown-style-menu ul li:hover,

.h-100p,

.bmc-ctr-main-wrapper,

.custom-color-page .orange-dim-bg-border,
.custom-color-page .sup-comment-curved-arrow,

.creator-each,
.tags:first-child, .tags:nth-child(2), .tags:nth-child(3)
{
    background: #363639 !important;
}

input, textarea, select,
.ctr-detail .form-control,
.dash-navbar,
.comment-wrapper,

.header-menu,

.btn-outline-orange,

.input-holder,
.disabled-field, .disabled-input,

.bmc-checkbox label,

.dash-input,

.intercom-k9w1th .e19wqfua1,

.bmc-footer,

.side-bar-tabs:hover,  .parent-menu.active-tab,

/*building*/
.publicNav,
.textInput, .autoResizeTextarea,
.boardGridItem, .commentComposer .composerForm,
.signupForm .topContainer .authButton:hover, .signupForm .topContainer .emailButton:hover,
.postVotes,
.adminNavAccountMenu .accountMenu .menuItems,


.dash-drop-holder, .dropdown-el.expanded, .bmc-btn-new,
.post-transparent-btn,
.bmc-3-form-elem .panel-heading,

.input-h-100, .panel-default,
.supporters-data,

.creator-footer,

.custom-color-page .checkmark_ques, .inactive-subscription-option,

.dropdown-menu, .currencies ul li,

/*New stuff?*/
.header-btn,
.check-slug-wrapper,

.modal-content input,
.project-emoji-saver .emojionearea,
.emojionearea .emojionearea-picker, .emojionearea .emojionearea-picker .emojionearea-filters, .emojionearea .emojionearea-picker .emojionearea-scroll-area .emojionearea-category-title,

.editor__content,
.bmc-dropdown-with-pop:after,
.payment-tab-disable,

.bmc-input-box,

.cover-img-wrap,

.br-r-12,

.br-dark-05-2px,

.creator-each:hover
{
    background: #2e2e2f !important;
}

.intercom-1oxqnv7, .intercom-wlajoz,

.currencies ul li:hover, .currencies ul li:active, .currencies ul li:focus,

/*New Stuff?*/
.emojionearea .emojionearea-picker .emojionearea-scroll-area .emojibtn:hover
{
    background: #363639 !important;
}

.custom-color-page .comment-add-wrap .flex-v-center,

.modal.in .modal-dialog,

.tp-locations-bg,

.add-comment-sec .emoji-wysiwyg-editor
{
    background-color: #12121354 !important;
}

.bg-0D0, .bg-0D0-h
{
  background: #ccc;
}

.gradient-your-story
{
  background-image: linear-gradient(#0000 0%,#3d3d3f 100%);
}

.dash-wrapper,

.bg-white, .note-editor.note-frame .note-editing-area .note-editable,

.payment-tab-disable

/*New stuff*/
.editor-title,

.support-pay-section .v-center-text-p,

.bg-fff
{
  background: #0000 !important;
}

.custom-color-page .creator-page-logo, .custom-color-page .coffelink-c-sec .r-tile-border,
.custom-color-page .head-logo
{
  background-color: #0000 !important;
}

.yellow-btn,
.footer-abs-content
{
  background: #615400;
}

.disabled-field, .disabled-input
{
  opacity: .25;
}

body, input, textarea, select, ::placeholder,
.emoji-wysiwyg-editor,
.small-text,
.ctr-pg-head,
.update-text,

.welcome-note,
.h1, .h2, .h3, h1, h2, h3,
.guideline-head,
.home-pg-ex-cat p,
.tweet-user-name,

.input-click,
.or-text,
.supporter-name,

.common-head, .navbar-nav.nav-link li a,
.tab-head,

.dash-tabs, .dash-tabs:active, .dash-tabs:focus,
.tab-ac, .coffee-count p:first-child, .fund-val, .share-note,

.msg-note, .thankyou-text p,

.intercom-1oxqnv7,

.ex-creator-name, .limit-line-1,
.ex-category p,

.color-black, .tweet-msg, .bmc-footer *,
.cred-form *,

.dotted-list *, .share-type,

/*building*/
.userLockup .userInfo .name,
.postBody p, .postBody span, .commentBody p, .commentBody span,
.signupForm .topContainer .authButton .icon, .signupForm .topContainer .emailButton .icon, .signupForm .topContainer .note,

.lg-box *, .color-1b2,
.dash-drop-holder *, .post-transparent-btn,
.creator-tab.active-tab,

.note-editor.note-frame .note-editing-area .note-editable,
.tab-head-med,
.no-border-input, .no-border-input :focus,

.no-featured-wrapper .creator-pg-head-content,
.color-1B2-i, .color-black-i,

.payment-tab-disable,

.dash-buttons .color-1b2-i, .dash-buttons .color-1b2-i:hover, .dash-buttons .color-1b2-i:active, .color-26-i, .color-1B2,

.curved-label-light,

.header-btn,

.m-b-12, .color-0d0,

.color-0D0,
.text-fs-18, .bmc-dash-pill:hover span, .bmc-dash-pill-active span,

/*New stuff?*/
.home-desc-24,
.home-slug-input, ::placeholder, [contenteditable][placeholder]:empty:before,

.home-text-16,
.scroll-text-bold,

.editor-title,
.editor__content,
.bmc-dropdown-style-menu ul li:hover *,

.post-title, .update-msg-edit p,
.post-creator .dash-dropdown li a,

.bio-para-creator p, .bio-para-creator div,
.clr-dark,
.custom-color-page .active-option,
.orange-on-hover.active-creator-tab,

.x-icon,

.post-s-header-sub .curved-label-own-color,

.cover-img-tab.active,
.orange-on-hover:hover, .orange-on-hover:focus,

.supp-wrapper a,

.clr-grey
{
  color: #ccc !important;
}

.lead.emoji-picker-container .emoji-wysiwyg-editor:empty:before
{
  color: #999 !important;
}

.side-bar-tabs,
.side-bar-tabs:hover,

.color-26-op
{
  color: #ccc;
}

.close-btn-pos path,
.br-r-8 path, .br-r-8 polygon[fill*="000000"]
{
  fill: #ccc;
}

*,
.dash-navbar,

.custom-color-page .sup-comment-box:after
{
  border-color: #0000 !important;
  box-shadow: none !important;
}

.b-shadow,
.dropdown-el.expanded
{
    box-shadow: 0 1px 2px rgb(24, 24, 24) !important;
}

.cover-img-tab.active
{
  border-bottom: 1px solid #ccc !important;
}

.m-r-8,

.payment-tab-disable svg,

.cover-img-tab.active svg
{
  filter: invert(1);
}

.extra-temp-each img,
.btn-group-sm>.btn, .btn-sm
{
  filter: invert(1);
  mix-blend-mode: lighten;
}

.publicNav .publicNavContent .secondaryNav .link .icon img,
.abs-drawn-text,

.m-l-10
{
    filter: invert(1) hue-rotate(180deg) saturate(6) brightness(6)
}

.vert-align-tet,

.m-r-6,

.bmc-3-form-elem .note-btn
{
    filter: invert(1) hue-rotate(180deg) saturate(.8) brightness(1);
}

.m-t-60 > svg,
.sidebar-options > svg,

.curved-label-light > div > svg
{
    filter: invert(1) hue-rotate(180deg) saturate(1) brightness(1)
}

.home-pg-cat-img,
.use-case-item > img,
.cat-img, .auth-close-btn,

.home-hero-img
{
    filter: invert(1) hue-rotate(180deg) saturate(6) brightness(6);
    mix-blend-mode: color-dodge;
}

.home-float-pattern
{
    filter: invert(1) saturate(0) brightness(6.9);
}
.home-float-pattern-bottom
{
    filter: invert(0) saturate(1) brightness(.241);
}

.ctr-update-wrapper .emoji-wysiwyg-editor,
.boardHome .postListContainer
{
  padding-left: 10px !important;
}

.b-shadow
{
  width: 15%
}

body > div.bmc-main-wrapper > div.dashboard-pages-wrapper.flex > div.dash-main-sec.w-100.m-b-main-wrap.md-w-100.p-l-20P
{
  width: 100% !important;
  margin-left: auto;
  margin-right: auto;
}

.intercom-namespace .intercom-pv83im
{
  box-shadow: 0px 0px 20px 0px #0000009e !important;
}

.postVotes .upvote {
    border-left: 9px solid transparent !important;
    border-right: 9px solid transparent !important;
    border-bottom: 9px solid rgba(209, 209, 209, .7) !important;
    margin-bottom: 3px !important;
}
.postVotes .upvote.voted
{
  border-bottom-color: #ff6311 !important;
}

body > div.home-container.m-0a.bmc-header > div > div.flex.w-65.md-w-auto > a > svg *
{
  fill: #0000 !important; 
  stroke: #0000 !important;
}

.post-s-header-sub .curved-label-own-color svg path,

* path[fill*="#0D0C22"], * path[fill*="black"]
{
  fill: #ccc
}

.head-logo
{
  background-image: url(https://i.imgur.com/3pzrYd7.png);
  background-size: contain;
  background-color: #0000;
    background-repeat: no-repeat;
    background-position-y: center;
}

.bmc-logo
{
  background-image: url(https://i.imgur.com/cnCaXMS.png);
  background-size: contain;
  background-color: #0000;
  background-repeat: no-repeat;
    background-position-y: center;
}

.logo-text svg > *,

.head-logo svg,
.bmc-logo *
{
  display: none;
}

audio, canvas, progress, video
{
  height: 25px;
}

.placehooler-animation.focus
{
  top:45px
}

.b-shadow
{
  margin-right: 40px
}

.m-t-25,
.emoji-picker-container,
.m-t-30
{
  margin-top: 0px;
}

.m-t-25,
.m-t-30,
.p-l-30
{
  padding-top: 30px;
  padding-bottom: 30px;
}

.editor-title
{
  padding-left: 10px !important;
}
}

@-moz-document domain("changelog-widget.canny.io") {
body, html, head,
html #content, .changelogWidgetContainer,
#canny-changelog-iframe
{
  background: #3d3d3f !important;
}

.changelogWidgetEntryListItem:hover
{
  background: #363639;
}

.changelogWidgetHeader,
.changelogWidgetHeader .buttonSpacer,

.changelogWidgetFooter
{
    background: #2e2e2f !important;
}

body, html,
.changelogWidgetHeader .heading
{
  color: #ccc;
}

*
{
  border: none !important;
  box-shadow: none !important;
}
}
