@-moz-document url-prefix("https://www.tumblr.com/edit/") {
/* GENERAL */

body {
    font-family: helvetica;
    font-size:12px!important;
}




/* BACKGROUND */

.identity {
    background-color: #202020;
}




/* TOP BAR */

.l-header-container--refresh {
    box-shadow: 0 0 0 1px hsla(0,0%,100%,.13);
    transform: translateZ(0) translateY(0);
    z-index: 90;
    background-color: #151515;
    position: fixed;
    width: 100%;
}


.l-header-container--refresh .l-header {
    box-sizing: border-box;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    position: relative;
    width: 100% !important;
    max-width: 1580px;
    left: 0;
    top: 0;
    min-width: inherit;
    padding-top:14px!important;
    height:82px;
}


.ui_search .search_form_field::before {
    transform: scaleY(.9);
    content: "\EA99";
    transition: color .15s;
    position: absolute;
    font-size: 29px;
    color: inherit;
    z-index: 2;
    left: 15px;
    top: 7px;
}


.ui_search .search_query {
    padding-right: 10px;
    padding-bottom: 2px;
    padding-left: 39px;
    line-height: inherit;
    font-family: helvetica !important;
    font-size: 12px;
    opacity:1!important;
    height: 34px;
    width: 100%;
    border: 0;
    border-radius: 3px;
    margin-top: 10px;
}


.tab_bar .tab_anchor {
    transition: opacity 0ms;
    margin-top: -2px;
    margin-right:15px!important;
    margin-left:16px!important;
    height: 35px;
    text-decoration: none;
    text-align: center;
    text-indent: 0;
    display: block;
    font-size: 0;
    color: #fff;
    float: right;
    opacity: .65;
    background: none;
    overflow: hidden;
}


.tab.iconic.tab_xkit {
    margin-top:0px;
    height:50px;
}


.compose-button {
    position: relative;
    cursor: pointer;
    height: 36px;
    width: 49px;
    margin-left: 16px!important;
    margin-right:-4px;
    margin-top: 9px;
    padding: 2px;
    float: right;
    z-index: 1;
    fill: #00b8ff;
}


#xoldeheader {
    margin-right:12px;
    margin-top:4px;
    display: inline-block;
}




/* LEFT COLUMN */


.l-container.l-container--two-column .left_column {
    float: left;
    width: 595px;
    margin-left:50px!important;
}


.l-container.l-container--two-column-dashboard .l-content, .l-container.l-container--two-column .l-content {
    background-color: #151515;
    margin-top:30px!important;
    margin-left:-68px!important;
    border-radius:25px!important;
    width:687px!important;
}


.left_column {
    margin-left: 75px;
    margin-top:1px;
}


.l-container.l-container--two-column-dashboard .left_column {
    background-color: #151515;
}


.post_avatar .post_avatar_link {
    background-color: transparent!important;
    display: block;
    height: 100%;
    width: 100%;
    border-radius: 3px;
    background-size: cover;
    overflow: visible;
}


.post_avatar {
    border-radius: 3px;
    background-color: transparent!important;
    background-size: contain;
    text-decoration: none;
    position: absolute;
    overflow: visible;
    display: block;
    height: 64px;
    width: 64px;
    left: -85px;
    top: 0;
}


.l-container.l-container--two-column .l-content {
    background-color: #151515;
    margin-top:31px;
    margin-left:-68px;
    border-radius:25px;
    padding-top:50px;
    padding-bottom:50px;
    width:687px;
}



.white_pane {
    overflow-x:hidden!important;
    background: #fff;
    border-radius: 6px;
    margin-left:-25px!important;
    padding: 28px;
    font-size: 16px;
    position: relative;
}


.title_and_controls + .followers .follower {
    padding-top: 10px;
    width:520px;
}




/* RIGHT COLUMN */


#right_column {
    background: none;
    width: 320px!important;    
    position:absolute;
    margin-top:-27px;
    margin-left:726px;
}


.identity .controls_section .open_blog .open_blog_link {
    color: #b1b1b1;
    font-size: 12px;
    font-weight:bold;
    margin-top:-2px!important;
}


.identity .controls_section .open_blog .currently_selected_blog {
    display: block;
    padding: 0;
    color: hsl(0, 0%, 100%);
    font-weight: 700;
    margin-bottom:-1px!important;
}


.identity .controls_section li.controls_section_item > a:first-of-type, .identity .controls_section li.controls_section_item > div:first-of-type {
    padding-right: 10px;
    padding-left: 10px;
    font-family: helvetica!important;
    font-size: 12px;
}

.identity .controls_section {
    border-top: 0px solid hsla(0,0%,100%,.07);
    position: relative;
    padding: 0;
}


.identity .controls_section li.section_header {
    border-bottom: 1px solid hsla(0,0%,100%,.13);
    background-color: transparent;
}


.identity .controls_section li.section_header {
    padding: 0 10px 4px;
    color: #fff;
    text-transform:lowercase!important;
    font-size: 12px;
    font-weight: 700;
    line-height: 25px;
}


.identity .controls_section li.section_header:first-letter {
    text-transform:uppercase!important;
}


.identity .controls_section a .count {
    position: absolute;
    top: 0;
    right: 10px;
    color: #a2a2a2;
    font-size: 12px;
    font-weight: bold;
}


.identity .controls_section .sub_control {
    position: absolute;
    right: 0;
    margin-right: 10px;
    margin-top:5px;
    padding: 1px;
    transform: translateY(50%);
    font-size: 12px;
    background: none;
    color: #a2a2a2;
    cursor: pointer;
}


.identity .controls_section li.section_header {
    border-bottom: 0px solid hsla(0,0%,100%,.13);
    background-color: transparent;
}


.identity .right_column::after {
    display:none!important;
}


.post.post_full .post_body {
    padding-right: 20px;
    padding-left: 20px;
    font-family: helvetica!important;
    font-size:12px!important;    
}


.identity .controls_section li.controls_section_item > a:first-of-type, .identity .controls_section li.controls_section_item > div:first-of-type {
    padding-right: 10px;
    padding-left: 10px;
    font-family: helvetica !important;
    font-size: 12px;
}


.indash_header_wrapper .info {
    padding-top:25px!important;
    text-align: center;
}


.tumblr_settings .setting.checkbox {
    font-family: helvetica !important;
    font-size:12px!important;
    margin-right: 50px;
    padding-left: 32px;
    position: relative;
}


.tumblr_settings--tumblelog .queue_group .queue_sentence {
    font-family: helvetica !important;
    font-size:12px!important;
    font-weight: bold;
    margin: 0 50px 10px 0;
}


.flat_select select {
    font-family: helvetica !important;
    font-size:12px!important;
    border: none;
    background: none;
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    width: 100%;
    height: 100%;
    opacity: 0;
}


.tumblr_settings .settings_group .group_content {
    display: table-cell;
    padding: 0 10px;
    vertical-align: top;
    font-family: helvetica !important;
    font-size:12px!important;
}


.tumblr_settings .setting {
    display: block;
    margin-bottom: 20px;
    position: relative;
}


.post-forms-glass {
    display: block;
    position: fixed;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 996;
    pointer-events: none;
    background-color: rgba(0, 0, 0, .85);
}
}
