css:
@-moz-document domain("artemis.ase.in.tum.de") {
/* Deaktiviert die Kommentarbox komplett !
div.col-sm-12.col-md-4.col-lg-4
{
    display: none !important;
}
*/
/* Verschiebt die Kommentarbox nach ganz unten!
.col-lg-8
{
    flex: 0 0 100% !important;
    max-width: 95% !important;
}
*/
.jh-navbar
{
    background-color: #1c1c1c !important;
    color: #ccc !important;
}

.card-body
{
    background-color: #1c1c1c !important;
    color: #ccc !important;
}

.page-wrapper
{
    background-color: #2d2d2d !important;
    color: #ccc !important;
}

.jh-card
{
    background-color: #1c1c1c;
}

.card-footer
{
    background-color: #2d2d2d;
    color: #ccc;
}

.card
{
    background-color: #3e8acc;
    border: 8px solid #1c1c1c;
}

.col-lg-3
{
    border-left: 1px solid #2d2d2d;
    border-right: 1px solid #2d2d2d;
}

.footer.fixed
{
    background-color: #1c1c1c;
}

.dropdown-menu
{
    background-color: #2d2d2d;
    color: #3e8acc;
}

.dropdown-item
{
    color: #3e8acc;
}

.dropdown-header
{
    color: #3e8acc;
}

.course-info-bar
{
    background-color: #1c1c1c !important;
    color: #ccc !important;
}

div.row.align-items-center.mb-2.mt-2
{
    background-color: #2d2d2d !important;
    border: 0px !important;
}

.card-second-header
{
    background-color: #1c1c1c !important;
}

[_nghost-qlj-c19] > div[_ngcontent-qlj-c19]
{
    background-color: #2d2d2d;
    border-color: #1c1c1c;
}

pre
{
    color: #ccc;
    background-color: #2d2d2d;
}

.modal-footer
{
    border-top: 1px solid #2d2d2d;
}

div.modal-content
{
    background-color: #1c1c1c;
    border: 1px solid #2d2d2d;
}

.modal-header
{
    border-bottom: 1px solid #3e8acc !important;
}

.popover
{
    background-color: #1c1c1c;
    color: #ccc;
}

.popover-body
{
    color: #ccc;
    border: 1px solid #3e8acc !important;
}

pre[_ngcontent-scp-c10]
{
    color: #ccc;
}

.close
{
    float: right;
    font-size: 1.75rem;
    font-weight: 500;
    line-height: 1;
    color: #ccc;
    text-shadow: 0 0px 0 #ccc;
    opacity: 1;
}

body
{
    color: #3e8acc;
}

pre
{
    border: 0px solid #ccc !important;
    color: #ccc !important;
}

.btn-outline-dark
{
    color: #3e8acc;
    border-color: #2d2d2d;
}

.text-warning
{
    color: #3e8acc !important;
}

.text-muted
{
    color: #ccc !important;
}

.table thead th
{
    vertical-align: bottom !important;
    border-bottom: 1px solid #ccc !important;
}

.table
{
    color: #3e8acc !important;
}

.navbar-dark .navbar-nav .nav-link
{
    color: #ccc !important;
}

.navbar-dark .navbar-brand,
.navbar-dark .navbar-brand:focus,
.navbar-dark .navbar-brand:hover
{
    color: #ccc !important;
}

.form-control
{
    color: #ccc !important;
    background-color: #2d2d2d !important;
    background-clip: padding-box !important;
    border: 0px solid #2d2d2d !important;
}

.form-control:focus
{
    color: #ccc !important;
    background-color: #2d2d2d !important;
    border-color: #2d2d2d !important;
    outline: 0 !important;
    box-shadow: 0 0 0 0rem #2d2d2d !important;
}

.text-black-50
{
    color: #3e8acc !important;
}

.panel-wrapper .panel-header
{
    background-color: #1c1c1c !important;
    color: #ccc !important;
}

.alert-info
{
    background-color: #1c1c1c;
    color: #ccc;
    border: 0px solid;
}

.text-black-50
{
    display: none !important;
}

.alert-success
{
    display: none !important;
}
}
