css:
@-moz-document url("https://sspai.com/") {
.main-banner-box,
.minor-banner-box,
.pai_title,
.img_box.pai_img_box,
.recommend-card,
.card_content .right,
.indexRight.index_right > div > a {
    display: none !important;
}

.articleCard-box > div {
  max-width: 48%;
  margin: 0 5px;
  display: inline-block;
  overflow-y: hidden;
  overflow-x: hidden;
}

.img_box {
  width: 300px !important;
}

.pai_content {
    padding: 0 !important;
}

.pai_card_content {
    width: 100% !important;
    padding: 10px !important;
}


.card_content .left {
  display: -webkit-box !important;
}

.card_content .title.text_ellipsis2 {
  margin-bottom: 5px !important;
}

.card_content {
    width: auto !important;
    padding: 10px !important;
}

.follow_title {
    margin: 0 0 10px !important;
}

.swiper-container {
    padding: 20px 0 0;
}

.advertisementCardBox:hover .card_content {
    transform: translateX(-2px) !important;
}
}
