css:
@-moz-document domain("userstyles.org") {
body, #nav-view-responsive {
  background-color: #222;
  color: #eee;
}

h1, h2, h3, h4,
#headline, .text_style_page, .style_title, .title_setting,
#infomation_value_left, #information_key_left, #information_key, #information_value,
#share_div, #applies_link {
  color: #ddd;
}

h4 a:visited, h4 a,
#pages a, #pages a:active, #pages a:visited {
  color: #ddd !important;
}

a, *:not(.notes_textarea):not(.text_style_page):not(.style_description_long):not(.us-username):not(.NotificationLine-Content) > a {
  color: #54b4f4;
}

.us-stylecard--short, .us-stylecard--long {
  background-color: #444;
}

.div-arrow {
  background-color: #444;
  border-color: #666;
}

.navigation {
  background-color: #222;
}

.navigation .links-box .navigation-link {
  box-shadow: 0 -1px 0 0 #888, 0 0 0 0 #888;
}

.navigation .links-box {
  box-shadow: 0 -1px 0 0 #888, 0 1px 0 0 #888;
}

.navigation .links-box .navigation-link:hover {
  background-color: #444;
}

.navigation .links-box .navigation-link .navigation-title {
  color: #ddd;
}

.navigation .links-box:not(:nth-child(3n)) .navigation-link .logo-navigation-link {
  filter: invert(100%);
}

#top_android_button, #top_android_button .robot_android {
  filter: invert(100%);
}

#footer {
  background-color: #000;
}

#footer #copyrights {
  color: #ddd;
}

#footer .footer_link a, .bottomLinks {
  color: #54b4f4 !important;
}

.white_button, .customize_button, #send_feedback_button {
  background-color: #444;
  border-color: #666;
  color: #ddd;
}

.white_button:hover, .customize_button:hover, #send_feedback_button:hover {
  background-color: #666;
}

#notes_textarea {
  margin-top: 10px;
  background-color: transparent;
  color: #ddd;
}

.css_button, .see_more, .personal-page .light-blue-button {
  background-color: #525a63;
  color: #54b4f4;
  border: 1px solid #888;
}

.css_button:hover, .see_more:hover, .personal-page .light-blue-button:hover {
  background-color: #69717b;
}

.style_category_card_wrapper {
  background-color: #444;
}

#category_style_card_title {
  color: #ddd;
}

.choose_editor {
  background-color: rgba(55, 56, 58, 0.92);
}

.choose_editor .boxesContainer .box {
  background-color: #444;
}

.choose_editor .boxesContainer .box .title {
  color: #ddd;
}

#create_new_style {
  color: #54b4f4 !important;
  border-color: #54b4f4;
  box-shadow: 0px 0px 4px 2px rgba(200, 200, 200, 0.5);
}

input, textarea, select, ::placeholder {
  filter: invert(100%);
}

.advancedsettings_shown {
  background-color: #444;
}

.header-search .search_input .input_text_search {
  background-color: #aaa0;
  color: #000;
}

.header-search .search_input {
  background-color: rgba(200, 200, 200, 0.24);
}

.header-search .search_input.focused {
  background-color: #666;
}

#overlay-info {
  background-color: rgba(0, 0, 0, 0.88);
}

.overlay_background {
  background-color: rgba(5, 7, 10, 0.96);
}

.errorExplanation {
  background-color: #844;
}

.OptionsTitle {
  filter: invert(100%);
}

#stylish-code {
  filter: invert(0%);
  border-color: #666;
}

code, pre {
  background-color: #009;
  border-color: #666;
}
}

@-moz-document domain("forum.userstyles.org") {
code, pre {
  background-color: #009;
  border-color: #666;
}

.DismissMessage, .InfoMessage, .AlertMessage,
.Sprite16 {
  filter: invert(100%);
}

.Button {
  background-color: #0f0f0f;
}

input#Form_PostComment {
  background: #fff;
}

.Item.Read {
  background-color: #333;
}
}
