css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://userstyles.org/) , url-prefix("https://userstyles.org/") {

  h1 { font-size: 16px; }
  h2 { font-size: 14px; }
  h3 { font-size: 12px !important; }
  .review-container blockquote {
    margin-top: -10px !important;
    margin-left: 10px !important;
    font-size: 11px;
  }
  .review-container h3 { margin-bottom: 3px !important; }
  span.stars img { height: 13px; }

  html, body {
    font-size: 12px !important;
  }
  #breadcrumbs, #other-links, h1, #install {
    background-color: #1E90FF !important;
  }
  #breadcrumbs, #other-links {
    height: 20px !important;
    color: #ffffff !important;
  }
  #install {
    border-color: #00BFFF !important;
    color: #ffffff !important;
  }
  #install li {
    list-style-type: none;
  }
  #install ul {
    padding-left: 8px !important;
  }
  #other-links + h1 {
    text-align: right;
    padding-right: 10px;
    margin-top:0 !important;
    color: #ffffff !important;
    font-style: italic;
    font-size: 14px !important;
  }
  #breadcrumbs a, #other-links a {
    font-size: 12px !important;
    color: #ffffff !important;
  }
}
