css:
@-moz-document url-prefix("https://www.timeanddate.com/") {
#header {background:#111;}
.main-content-div > div.row.socrow{background:#222}
.main-content-div{background:#111;color:#fff;}
.main-content-div > section.bg-grey.pdflexi.is-shared{background:#222}
.main-content-div > header{display:none;}
.bg-grey.pdflexi.is-shared > div > div:nth-child(3) > h3{display:none;}
.bg-grey.pdflexi.is-shared > div > div:nth-child(3){background:#292929;border-color:#191919}
#closeme4{display:none;}
.main-content-div > section:nth-child(7){display:none;}
html {background:#111}
#footer{display:none;}
.nav-tabs__wrap > div > nav {color:#222;background:#333;box-shadow: inset 0 -5px 0 #333!important;}
.nav-tabs__item.active{color:#fff;background:#222!important;}
body > div.wrapper > div.main-content-div > section.bg--grey.pdflexi-t--small > div{background:#222;}
body > div.wrapper > div.main-content-div > section.bg--grey.pdflexi-t--small{background:#222}
body > div.wrapper > div.main-content-div > section.bg--grey.pdflexi-t--small > div > section > div:nth-child(2) > div > table{color:#fff;background:#333;}
.zebra tr.c1, .zebra tr:nth-child(2n){background:#222;}
body > div.wrapper > div.main-content-div > section.bg--grey.pdflexi-t--small > div > section > div:nth-child(2) > div > table > thead > tr > th{background:#333;}
#msg,#font,#p0txt,#day,#month,#year,#hour,#min,#sec {background:#333;}
#countdown-form {background:#222;color:#fff;}
}
