@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
domain("hattrick.ws"),
domain("hattrick.interia.pl"),
domain("hattrick.uol.com.br"),
domain("hattrick.org") {


div.coachInfo, div.playerInfo, div.managerInfo, div.arenaInfo,div.faceCard {
padding:0!important;
margin:0!important;
}

div.playerInfo p, div.managerInfo p {
line-height:1.1em !important;
margin: 2px 0 0 !important;
}

div.playerInfo  {
margin:  5px  0 0 15px !important;
}

div.borderSeparator {
height:3px!important;
margin-bottom:3px!important;
}

div.playerList h2.category{
margin:0!important;
}



}
