.experience-default .plp-pod,
.experience-technical .plp-pod {
	width: 23%;
}

.col__3-12 {
	width: 22%;
}

.experience-inspirational .plp-pod {
	width: 21%!important;
}

.related_articles_link-wrapper {
    width: 36rem;
}

.reviews,
