css:
@-moz-document url-prefix("https://beta.protonmail.com/") {
body, .bodyDecrypted table, .bodyDecrypted, pre, p, font {
	font-family: 'Open Sans', "Lucida Grande", Verdana, Arial, sans-serif !important;
    font-weight: 300 !important;
	font-size: 100%;
}

body {
	font-size: 130%;
}

.conversationHeader-header .ng-binding {
	font-size: 80% !important;
    padding-left: 5px !important;
}

.displayContentBtn-notice-text {
}

.message-infobox {
	opacity: 0.5 !important;
	font-size: 80% !important;
}

[data-name='trash'], [data-name='delete'] {
	fill: #d94949ab !important;
}

[data-name='inbox'] {
	fill: #369bf4ab !important;
}

[data-name='archive'] {
	fill: #85e685ab !important;
}

[data-name='spam'] {
	fill: #ff7800 !important;
}

[data-name='search'], .btnAdvancedSearch-icon-desktop {
	opacity: 0.2;
}

[id="sidebarLabels"] {
	margin-right: 14px;
}

.folderSelectorDropdown-icon use {
	fill: var(--fillcolor-icons,#e7d292) !important;
}

.conversation:not(.read):not(.active) .senders,
.conversation:not(.read):not(.active) .subject-num-message,
.conversation:not(.read):not(.active) .subject-text,
.conversation:not(.read):not(.active) .time {
 font-weight:600 !important;
}

.bold, .strong, b, strong {
 font-weight:600 !important;
}

[aria-current=page].navigation__link {
 font-weight:600 !important;
}


.headerSecuredDesktop-logo,
.topnav-list .mr1, #pm_sidebar .pm-button-blue, #aside-bar {
	opacity: 0.3 !important;
    z-index: 100 !important;
}

.sidebar {
	max-width: 16rem !important;
}

.items-column-list {
	width: calc((85% + var(--width-sidebar, 19rem))*0.35);
}

.time {
	font-size: 90% !important;
    opacity: 0.7 !important;
}

.labels .navigation__link {
	padding: 0.5rem 0.4rem 0.5rem 2rem !important;
}

#labelSettings {
    display: none !important;
}

.encryptionStatus .icon-16p {
	opacity: 0.5 !important;
    width: 12px !important;
    height: 12px !important;
    position: relative !important;
    top: 4px !important;
    left: 2px !important;
}

.labelsElement-name {
	text-transform: none;
    font-size: 90%;
}

.messagePlaceholder, #pm_placeholder {
	opacity: 0.3 !important;
}

#conversation-list-columns .conversation.active .item-titlesender{
    color: #fff;
}

[ng-if="showWelcome"] {
	opacity: 0.2;
}
}
