css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {

.svg-logo,
.logo .png-logo,
.logo:hover .opacity-switch .preload-container {
	background: url('https://i.imgur.com/zlCLr1y.png*[[logo1]]*//*[[logo2]]*/ /*[[logo3]]*/')!important;
	background-size: contain !important;
	background-repeat: no-repeat !important;
	width: 195px !important;
	height: 50px;
	/*	position: absolute; top: 0px;*/
	padding: 90px 90;
	margin-right: 13px;
	left: 10px;
	margin-top: 9px;
}

.logo:hover .opacity-switch .png-logo {
	opacity: 0.7;
}

.opacity-switch .preload-container {
	display: none;
}

.tab_bar {
	right: 10px!important;
}

.popover_menu_item .thumb {
	right: 33px !important;
}

.search_popover * {
	box-sizing: border-box;
	width: 188px;
}

.logo .large-shadow,
.logo .small-shadow,
.logo .outer,
.logo .inner {
	display: none!important;
}
}
