/* By http://friendfeed.com/thedod
   A minimalistic variation on "Friendfeed Red and Black"
   http://userstyles.org/styles/17137
*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("friendfeed.com") {

    body {
        background: black !important
    }

    #bookmarkletform {
        background: white !important ;
        padding: 4px !important
    }


    #search a, #profile a, .promolinks a, #footer, #footer a {
        color: white !important
    }

    #logo {
        background: url(http://i.friendfeed.com/58c2d3571a034c5a62f9aa8918d5cda10fc404bf) no-repeat !important
    }

    #logo img { opacity: 0 !important }

}
