@-moz-document domain("steamcommunity.com"), domain("store.steampowered.com") {
 ::-webkit-scrollbar {
 	/* Width */
 	width: 1.2em!important;
 }

 /* Color of handle ___________________________________________________________________________*/

 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track ______________________________________________________________________________*/

 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom ___________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }
}

@-moz-document domain("steamcommunity.com"), domain("store.steampowered.com") {
/*__________________________________________________________________________________*/

* {
	text-decoration: none!important;
}

div#global_header .menuitem,
div#global_header .submenuitem
 {
	text-transform: capitalize!important;
}

.searchbox,
.dailydeal_ctn,
.apphub_CardContentAuthorBlock,
.apphub_responsive_tab .responsive_tab_control,
.apphub_sectionTab.active,
.apphub_sectionTab.active:hover,
.community_home_search_apps,
.community_home_search_players,
.forum_op,
.miniprofile_favorite_badge,
.miniprofile_top,
:focus
 {
	border: none!important;
	outline: none!important;
	box-shadow: none!important;
}

/*___ Fonts ________________________________________________________________________*/

*,
h1, h2, h3, h4, h5, h6,
body,
html {
font-family: "Fura Code Nerd Font",
        "PragmataPro Nerd Font",
        "Input Mono",
        "Monoid",
        "Monoid Halftight",
        "Monoisome",
        "Fura Code",
        "Fira Code",
        Consolas,
        "Liberation Mono",
        Menlo,
        Courier,
        monospace
        !important;
}

selector
 {
	font-size: 21px!important;
}

.apphub_CardContentNewsDesc,
.apphub_UserReviewCardContent .apphub_CardTextContent,
.commentthread_comment_author a,
.discount_block .discount_final_price,
.tab_item_name
 {
	font-size: 19px!important;
}

.tab_item_discount .discount_final_price,
.apphub_CardContentGuideDesc,
.appHubShortcut_Title,
body 
 {
	font-size: 17px!important;
}

.game_hover_box .hover_release,
.discount_block .discount_original_price,
.commentthread_comment_text,
.new_items_num,
div#global_header .submenuitem
 {
	font-size: 15px!important;
}

.tab_item_name,
.ssa_box p,
.new_items_num
 {
	line-height: 25px!important;
}

/*___ Remove Items _________________________________________________________________*/

selector
 {
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/*___ Left/Right/Top/Bottom ________________________________________________________*/

.apphub_CardContentAuthorBlock {
    top: 0;
}

.apphub_CardMetaData {
    bottom: 0;
}

.apphub_CardContentAuthorBlock {
    left: 64%;
}

.forum_comment_permlink {
    right: 14px;
    bottom: 13px;
}

.home_ctn {
    margin-top: 1rem;
}

div.rightcol.small {
    margin-top: -84rem!important;
	margin-right: -12rem!important;
}

.home_ctn,
.home_cluster_ctn {
    margin-left: 17rem;
    margin-right: 2rem;
}


body:not(.no_home_gutter) .home_page_gutter
 {
	margin-left: -11rem!important;
}
div.leftcol {
    margin-left: -12rem;
}

.ssa_box ol {
    margin-left: 71px;
}

/*___ Margins ______________________________________________________________________*/

.apphub_Content,
.community_home_header,
.community_home_tabs,
.contenthub_page .page_header_ctn,
.discussions_forum_row,
.forumtopic_comments .commentthread_comment,
.maincontent,
.whyJoinLeft,
.whyJoinRight,
div.rightcol
 {
	margin: 12px!important;
}

.forum_topic
 {
	margin: 12px 6px!important;
}

/*___ Paddings _____________________________________________________________________*/

#tab_preview_container,
.apphub_Content,
.contenthub_page .page_header_ctn,
.maincontent,
a.recent_game_discussion,
body:not(.no_home_gutter) .home_page_content,
body:not(.no_home_gutter) .home_page_gutter,
div.leftcol,
div.rightcol
 {
	padding: 12px!important;
}

selector
 {
	padding: 3px 6px!important;
}

/*___ Heights ______________________________________________________________________*/

selector
 {
	max-height: none!important;
}

.apphub_Card.interactable,
.apphub_CardContentGuideDesc,
.apphub_CardContentMain
 {
	height: auto!important;
}

.apphub_CardRow[id*="template_"]
 {
    height: auto!important;
}

/*___ Widths _______________________________________________________________________*/

#mainBody,
.apphub_Content,
.community_home_header,
.community_home_sort_options,
.community_home_tabs,
.forum_op,
.group_content
 {
	max-width: none!important;
}


ppp {
	width: auto!important;
}

.leftcol
{
    width: 100%!important;
}


.maincontent,
div#global_header .content
 {
	width: 96%!important;
}

.community_home_header
 {
	width: 96%!important;
}

.form_notes,
.forum_list_ctn .forum_list_left,
.home_ctn,
.home_page_content,
body.v6 .page_content
{
    width: 78%!important;
}

#mainBody,
.mainLoginRightPanel
{
    width: 53%!important;
}

.apphub_Card {
    width: 48%!important;
}








}

@-moz-document domain("steamcommunity.com"), domain("store.steampowered.com") {
/*__________________________________________________________________________________*/
a[href],
td span {
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited {
