a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

.apphub_CardContentGuideDesc,
.apphub_CardContentNewsDesc,
.apphub_CardRating,
.apphub_UserReviewCardContent .apphub_CardTextContent,
.community_home_subtitle,
.contenthub_subtitle,
.discussions_forumlist_topiccount,
.forumtopic_comments .commentthread_comment,
.miniprofile_content,
.profile_count_link_total,
.profile_header_content,
.recent_game_discussion_count,
.tab_item_details,
li,
p
 {
	color: #fff!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Khaki ________________________________________________________________________*/

.apphub_CardContentNewsTitle,
.apphub_CardContentType.guide,
.commentthread_comment_author a,
.community_home_title,
.discussions_forumlist_header,
.friend_status_offline a,
.friend_status_offline div,
.friend_status_offline,
.game_name,
.persona, a.persona, .persona a,
.profile_customization_header,
.profile_header .persona_name,
.recent_game_discussion_name,
.rightbox_content_header,
.rightbox_list_header,
.tab_item_name,
.topic_hover_text,
body.tag_page h2.pageheader,
h1,
h2
 {
	color: #F0E890!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Burlywood ____________________________________________________________________*/

.apphub_CardContentAuthorName,
.miniprofile_favorite_badge .favorite_badge_description,
.rightSectionTopTitle,
.topic_hover_row,
h3,
h4
 {
	color: #e0b880!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Sandy Brown __________________________________________________________________*/

.hover_body,
.community_home_app_shortcuts_list_header,
.community_home_search_title,
.forum_audit,
.showcase_stat .label,
h5,
h6,
label
 {
	color: #F0A060!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Blue _________________________________________________________________________*/

.discount_block .discount_pct, .discount_pct,
.game_hover_box .hover_release,
.apphub_CardContentMain .apphub_CardTextContent .date_posted,
.apphub_CardContentNewsDate,
.commentthread_comment_timestamp,
.discussions_forumlist_lastpost,
.forum_audit span,
.topic_hover_row span:not(.topic_hover_data),
time
 {
	color: #07beff!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Green ________________________________________________________________________*/


.discount_block .discount_final_price
 {
	color: #07ff24!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Red __________________________________________________________________________*/

.discount_block .bundle_base_discount,
.discount_block .discount_original_price
 {
	color: #ff0707!important;
	
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea {
	background-color: #1d0909!important;
	border-radius: 22px!important;
	color: #fff!important;
	padding-left: 6px!important;
}

/*__________________________________________________________________________________*/

::selection {
	background: #ff9!important;
	color: #000!important;
}

pre ::selection {
	background: #ff9!important;
	color: #fff!important;
}

::placeholder { /* Chrome, Firefox, Opera, Safari 10.1+ */
  color: #f48f8f;
  opacity: 1; /* Firefox */
}

/*___ Misc _________________________________________________________________________*/

.apphub_UserReviewCardContent .found_helpful
 {
    color: #f8f6f6!important;
}

ppp {
    color: #af0!important;
}

ppp
 {
    color: #00b3ff!important;
}

ppp
 {
    color: #ff6cbe!important;
}
























}

@-moz-document domain("steamcommunity.com"), domain("store.steampowered.com") {
/*__________________________________________________________________________________*/

