div.leftcol:hover,
.contenthub_page .page_header_ctn.h
#tab_preview_container:hover,
.home_ctn:hover,
.home_cluster_ctn:hover,
body:not(.no_home_gutter) .home_page_gutter:hover,
.checkout_content_box:hover,
.block_content_inner:hover,
.whyJoinRight:hover,
.whyJoinLeft:hover,
.mainLoginPanel:hover,
.apphub_Content:hover,
.commentthread_comment:hover,
.community_home_header:hover,
.community_home_tabs:hover,
.maincontent:hover
 {
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

