.popup_block_new,
.tab_item,
.game_hover_box,
.popup_block_new .popup_body,
.apphub_Card,
.discussions_forum_row,
.forum_topic,
.forum_topic_tooltip,
.forumtopic_comments .commentthread_comment,
.miniprofile_hover_inner.shadow_content,
.supernav_content,
a.recent_game_discussion
 {
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

