css:
/***
* Author: Ultrabenosaurus
* Source: https://userstyles.org/users/867056 
*/
@-moz-document domain("wuxiaworld.site") {
html, body {
    background-color: #1a1a1a;
}
html, body, span, p, div, h4, h5,
body.search .search-wrap .tab-content-wrap .c-tabs-item .c-tabs-item__content .tab-summary .post-content .post-content_item .summary-heading h5 {
    color: #eeeeee;
}
a, 
.site-header .c-sub-header-nav .c-sub-nav_wrap .sub-nav_content .sub-nav_list li a, 
div.post-rating div span.score.font-meta.total_votes,
body.search .search-wrap .tab-content-wrap .c-tabs-item .c-tabs-item__content .tab-meta .rating .score,
body.manga-page .content-readmore,
body.manga-page .chapter-readmore,
body.search .search-wrap .tab-content-wrap .c-tabs-item .c-tabs-item__content .tab-summary .post-content .post-content_item .summary-content:not(.release-year) a {
    color: #aaaaaa;
}
div.profile-manga,
div.c-search-header__wrapper {
    filter: invert(1);
}
div.profile-manga div.container,
div.c-search-header__wrapper div.container {
    filter: invert(1) !important;
}

body.manga-page div.summary__content.show-more::after {
    background: -moz-linear-gradient(top, rgba(255, 255, 255, 0), #343a40);
}

header div.c-sub-header-nav.with-border.hide-sticky-menu,
div.tab-summary div.summary_image,
div.tab-summary div.summary_content_wrap,
div.tab-summary div.summary_content_wrap div.summary_content {
    background-color: #1a1a1a !important;
}
body.search .search-wrap .tab-content-wrap .c-tabs-item .c-tabs-item__content .tab-summary .post-content,
body.search .search-wrap .tab-content-wrap .c-tabs-item .c-tabs-item__content div.col-8.col-sm-10.col-md-10,
body.search .search-wrap .tab-content-wrap .c-tabs-item .c-tabs-item__content {
    background-color: #222222;
    padding-top: 15px;
}
}
