css:
@-moz-document domain("cordova.apache.org") {
body, .page-content, .page-content-container{
    background-color:#282828;
    color:#ddd;
}

code {
    background-color: #0008;
    color:#eee;
}

.highlight pre {
    border-color: #444;
    background-color:#0000;
    color:inherit;
}


.highlight {
    background-color:#1c1c1c;
    color:#eee;
}

.highlight .nt {
    color: #26c
}

.highlight .nb {
    color: #8c4;
}


footer {
    background-color: #0c0c0c;
}

.blue-divider{
    background-color: #09b;
}


.table-bordered > thead > tr > th, .docs table > thead > tr > th, .contribute-help table > thead > tr > th, .table-bordered > tbody > tr > th, .docs table > tbody > tr > th, .contribute-help table > tbody > tr > th, .table-bordered > tfoot > tr > th, .docs table > tfoot > tr > th, .contribute-help table > tfoot > tr > th, .table-bordered > thead > tr > td, .docs table > thead > tr > td, .contribute-help table > thead > tr > td, .table-bordered > tbody > tr > td, .docs table > tbody > tr > td, .contribute-help table > tbody > tr > td, .table-bordered > tfoot > tr > td, .docs table > tfoot > tr > td, .contribute-help table > tfoot > tr > td{
    border-color:#444;
}

.table-bordered, .docs table, .contribute-help table{
    border-color: #444;
}

.form-control {
    background: #000;
    color: #ddd;
    border-color:#444;
}
.form-control:focus{
    border-color:#09b;
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 8px rgba(102, 175, 233, 0.3);
}
}
