@-moz-document domain("freeimages.com") {
 ::-webkit-scrollbar {
 	/* Width */
 	width: 1.2em!important;
 }

 /* Color of handle ___________________________________________________________________________*/

 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track ______________________________________________________________________________*/

 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom ___________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }
}

@-moz-document domain("freeimages.com") {
/*__________________________________________________________________________________*/

* {
	text-decoration: none!important;
}

.btn,
.jumbotron-getbackground,
.jumbotron-tabs>a,
.navbar-inverse .nav>li>a,
.text-uppercase {
	text-transform: capitalize!important;
}

.btn-mode,
.btn-outline,
.iswitch .switch-off,
.line-vertical,
.listing-nav .caption,
.listing-tabs>a,
.listing-toolbar:first-child,
.panel,
.thumbnail-premium-sponsor .after,
.thumbnail-premium-sponsor .before,
.thumbnail-premium-sponsor,
.thumbnail-zoom:hover img,
:focus {
	border: none!important;
	outline: none!important;
	box-shadow: none!important;
}

/*___ Fonts ________________________________________________________________________*/

h4 {
	font-size: 19px!important;
}

.detail-info-group>li,
body {
	font-size: 17px!important;
}

.detail-info-group span {
	font-size: 15px!important;
}

/*___ Remove Items _________________________________________________________________*/

.sponsor {
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/*___ Left/Right/Top/Bottom ________________________________________________________*/

.mb20 {
	margin-top: 3rem!important;
}

/*___ Margins ______________________________________________________________________*/

.collage-body,
.panel {
	margin: 12px!important;
}

.detail-info-group>li {
	padding-left: 6px;
}

/*___ Paddings _____________________________________________________________________*/

.thumbnail-group>li,
.thumbnail-rowgrid {
	padding: 12px!important;
}

/*___ Heights ______________________________________________________________________*/

.thumbnail-group>li {
	height: auto!important;
}

/*___ Widths _______________________________________________________________________*/

body {
	max-width: none!important;
}

.thumbnail-group>li {
	width: 15%!important;
}
}

@-moz-document domain("freeimages.com") {
/*__________________________________________________________________________________*/

a[href],
td span {
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited {
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

.autocomplete-suggestions strong,
.pagination>.active>a,
a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label {
	color: #ff0!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

.autocomplete-suggestion span,
.autocomplete-suggestion,
.detail-info-group span,
.myfiles-group .thumbnail-text dl dd a:not(.btn),
.myfiles-group .thumbnail-text dl dt,
.tooltip.error .tooltip-inner,
body,
li,
p {
	color: #fff!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Khaki ________________________________________________________________________*/

h1,
h2 {
	color: #F0E890!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Burlywood ____________________________________________________________________*/

h3,
h4 {
	color: #e0b880!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Sandy Brown __________________________________________________________________*/

h5,
h6,
label {
	color: #F0A060!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Blue _________________________________________________________________________*/

time {
	color: #07beff!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea {
	background-color: #1d0909!important;
	border-radius: 22px!important;
	color: #fff!important;
	padding-left: 6px!important;
}

/*__________________________________________________________________________________*/

::selection {
	background: #ff9!important;
	color: #000!important;
}

pre ::selection {
	background: #ff9!important;
	color: #fff!important;
}

::placeholder {
	/* Chrome, Firefox, Opera, Safari 10.1+ */
	color: #f48f8f;
	opacity: 1;
	/* Firefox */
}
}

@-moz-document domain("freeimages.com") {
/*__________________________________________________________________________________*/

body,
html {
	background: url(https://image.ibb.co/hVmiOe/wallpaper_with_black_background_45_free_wallpaper.jpg)!important;
	background-attachment: fixed!important;
}

.modal-backdrop {
	background-color: #000;
}

/*___ Background Gray ______________________________________________________________*/

.footer,
.navbar-inverse .navbar-header,
.navbar-inverse {
	background-color: #060505!important;
}

.panel-heading {
	background-color: #0a0909!important;
}

/*___ Background-color 0000 ________________________________________________________*/

.autocomplete-suggestion:nth-child(even),
.autocomplete-suggestion:nth-child(odd),
.iswitch .switch-off,
.iswitch .switch-on,
.iswitch,
.listing-nav .caption,
.listing-tabs>a,
.modal-content,
.modal-footer,
.navbar-inverse .dropdown-menu>li>a:focus,
.navbar-inverse .dropdown-menu>li>a:hover,
.navbar-inverse .dropdown-menu>li>a:hover,
.navbar-inverse .navbar-nav>li:hover>a,
.navbar-inverse .navbar-nav>li:hover>a:focus .navbar-inverse .navbar-nav>li:hover>a:hover,
.pagination>.active>a,
.pagination>.active>a:focus,
.pagination>.active>a:hover,
.pagination>.active>span,
.pagination>.active>span:focus,
.pagination>.active>span:hover,
.pagination>li>a:focus,
.pagination>li>a:hover,
.pagination>li>span:focus,
.pagination>li>span:hover,
.premium-item .premium-img,
.thumbnail,
.thumbnail-group-line .thumbnail,
.thumbnail-premium-sponsor .after,
.thumbnail-premium-sponsor .before,
.thumbnail-premium-sponsor,
.thumbnail-text,
.userdetail-lightbox .caption {
	background-color: rgba(0, 0, 0, 0)!important;
}

/*__ Blue __________________________________________________________________________*/

.btn {
	background-image: linear-gradient(to bottom right, #043dda, #000)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

.btn:hover {
	background-image: linear-gradient(to bottom right, #154eeb, #000)!important;
	color: #ff0!important;
}

.btn:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*___ Plain Black __________________________________________________________________*/

.collage-body,
.panel {
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

.collage-body:hover,
.panel:hover {
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*___ Plain ________________________________________________________________________*/

.autocomplete-suggestions,
.modal.in .modal-dialog,
.myfiles-setitem .userdetail-lightbox,
.navbar-inverse .dropdown-menu,
.premium-preview-show,
.thumbnail-group>li {
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

.autocomplete-suggestions:hover,
.modal.in .modal-dialog:hover,
.myfiles-setitem .userdetail-lightbox:hover,
.navbar-inverse .dropdown-menu:hover,
.premium-preview-show:hover,
.thumbnail-group>li:hover {
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*___ Red Gradient _________________________________________________________________*/

.iswitch .switch-right,
.navbar-qards-inverse .tooltip.error .tooltip-inner {
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}

/*__ Green Gradient ________________________________________________________________*/

.iswitch .switch-left,
.listing-tabs>a.active,
.navbar-inverse .navbar-nav>.active>a {
	background: linear-gradient(to bottom, #15534d 0%, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	text-shadow: 1px 1px #555!important;
}

/*___ Highlight Background _________________________________________________________*/

.autocomplete-suggestion:nth-child(even):hover,
.autocomplete-suggestion:nth-child(odd):hover,
.category-group li a:hover,
.detail-info-group>li:hover {
	background: linear-gradient(to right, #383838, #0000)!important;
	border-left-color: #434343!important;
}

/*___ Highlight Background Active _________________________________________________________*/

.navbar-inverse .dropdown-menu>.active>a,
.navbar-inverse .dropdown-menu>.active>a:hover,
.navbar-inverse .dropdown-menu>.active>a:focus,
.category-group li.active a,
.category-group li.active a:hover {
	background: linear-gradient(to right, #014718, #0000)!important;
	border-left-color: #434343!important;
}
}

@-moz-document domain("freeimages.com") {
/*__________________________________________________________________________________*/

.autocomplete-suggestion,
.premium-item,
.thumbnail-group>li,
.thumbnail-rowgrid>.item,
.thumbnail-rowgrid>li
 {
	filter: brightness(75%)!important;
}

/*__________________________________________________________________________________*/

.autocomplete-suggestion:hover,
.premium-item:hover,
.thumbnail-group>li:hover,
.thumbnail-rowgrid>.item:hover,
.thumbnail-rowgrid>li:hover
 {
	filter: brightness(100%)!important;
}

/*__________________________________________________________________________________*/

img:not([src="https://images.freeimages.com/static/images/logo.png"])
 {
	border-radius: 22px!important;
}

/*__________________________________________________________________________________*/

img
 {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

}

@-moz-document domain("freeimages.com") {
.fa-adjust:before,
.fa-thumbs-down:before,
.fa-thumbs-up:before {
	content: "";
}

.detail-govern a .fa {
	font-size: 29px;
}

/* Adjust Contrast _______________________________________________________________*/

.fa-adjust:before {
	background: url(https://i.ibb.co/QHcs61G/contrast.png) no-repeat!important;
	box-sizing: border-box;
}

.fa-adjust:before {
	background-size: 29px!important;
	padding-left: 29px!important;
}

/* Avatar _______________________________________________________________*/

[src="https://images.freeimages.com/static/old/images/avatardefault.png"] {
	background: url(https://i.ibb.co/g3wQg00/avatar-32x32.png) no-repeat!important;
	box-sizing: border-box;
}

[src="https://images.freeimages.com/static/old/images/avatardefault.png"] {
	background-size: 52px!important;
	height: 66px!important;
	padding-left: 52px!important;
	width: 10px!important;
}

/* Freeimages Logo _______________________________________________________________*/

[src="https://images.freeimages.com/static/images/logo.png"] {
	background: url(https://i.ibb.co/t47Yfr9/freeimages-logo.png) no-repeat!important;
	box-sizing: border-box;
}

[src="https://images.freeimages.com/static/images/logo.png"] {
	background-size: 189px!important;
	padding-left: 199px!important;
	height: 36px;
	width: 27px;
}

/* Thumbs _______________________________________________________________*/

/* Up ________________________________*/

.fa-thumbs-up:before {
	background: url(https://i.ibb.co/MpqLxDN/Thumbs-Up-Skin-Color.png) no-repeat!important;
	box-sizing: border-box;
}

.fa-thumbs-up:before {
	background-size: 22px!important;
	padding-left: 24px!important;
}

/* Down ________________________________*/

.fa-thumbs-down:before {
	background: url(https://i.ibb.co/6XfG8VP/thumbs-dowm.png) no-repeat!important;
	box-sizing: border-box;
}

.fa-thumbs-down:before {
	background-size: 22px!important;
	padding-left: 24px!important;
}
}
