css:
@-moz-document domain("ayuppie.it")
{

/*sottotitoli*/
.menu-subtitle.light .menu-cnt li a span {
    color: 
    rgb(0, 0, 0);
}

/* barra in alto */
#nav {
    background-color: 
    rgb(0, 0, 0) !important;
}

.dropdown > ul, .dropdown-submenu > ul, nav .mega-menu {
    background: 
rgb(186, 186, 186) !important;
}

.dropdown ul li a {
    background-color: 
    rgb(186, 186, 186) !important;
}

.light .dropdown > ul > li:not(:hover) > a, .dropdown > ul > li a, nav:not(.light) a.btn-login.btn-border:hover {
    color: black !important;
}

/* Hover testo barra il alto*/
.menu-cnt > ul > li.active > a , .menu-transparent .menu-cnt > ul > li:hover > a {
    color: rgb(0, 243, 255) !important;
}

.menu-transparent .menu-cnt > ul > li > ul > li > a:hover {
    color: 
    #fff !important;
}


/* home */
/* <section id="services" class="section-image hc-cmp-section  light" style="background-image: url(https://ayuppie.it/wp-content/uploads/2019/08/bg.svg);"> */
section[style="background-image: url(https://ayuppie.it/wp-content/uploads/2019/08/bg.svg);"]{
    background-image: url(https://dumpshare.net/images/4780370bg2.png) !important;
}

/*colori home*/
.light, .light h1, .light h2, .light h3, .light h4, .light h5, .light h6, .light p, .light .btn-text, .light .btn-text:hover, .light .btn a, .light .btn:hover, .light .glide__arrow, .light .counter .value span:last-child, .light.counter .value span:last-child, .light .icon-links a i, .social-colors.icon-links-grid i, .icon-links-button a i {
    color: 
    #000 !important;
}

.light p, .light .btn-border:hover, .media-box-full .caption .extra-field, .media-box-reveal p, .light .accordion-list > li > a, .media-box p {
    color: 
    rgb(0, 0, 0) !important;
}

.light .glide__bullets > button, .light .glide__bullets > button.glide__bullet--active, .section-color .boxed-area, .bg-white {
    background-color: 
    #A5B3B7 !important;
}

/* team */
.cnt-box-team.boxed, .section-color .cnt-box.boxed .caption, .section-color .cnt-box-top-icon.boxed > i {
    background-color: 
    #b9b9b9 !important;
}

.cnt-box-side .extra-field {
    color: 
    #fff !important;
}

/* footer */
body > nav.light:not(.menu-transparent), nav.light.menu-transparent.scroll-menu, footer {
    background-color: 
    rgb(0, 0, 0) !important;
}
}
